        InboundFileFormats.caption("@SKS:SKGAPIAPEFT");
        SKS_EFT_InboundFileGroups_InboundFormatId1.label("@SKS:SKGAPIInboundAPI");

        //FileComplete.caption("@SKS:SKGAPIOutboundCompletionOptions");
        //SKS_EFT_InboundFileGroups_AutoFileCompletion.visible(false);
        //SKS_EFT_InboundFileGroups_SKS_EFT_AutoRecordCompletion.visible(true);

        //SKS_EFT_InboundFileGroups_ds.object(fieldNum(SKS_EFT_InboundFileGroups, AutoFileCompletion)).visible(false);
        //SKS_EFT_InboundFileGroups_ds.object(fieldNum(SKS_EFT_InboundFileGroups, SKS_EFT_AutoRecordCompletion)).visible(true);

        OutboundFileStatusUpdate.caption("@SKS:SKGAPIInboundStatusAction");
        ReferenceGroupFormat.label("@SKS:SKGAPIInboundAPI");

        ReferenceGroup1.label("@SKS:SKGAPIInboundAPI");

        if (element.args().record() && element.args().dataset() == tableNum(SKS_EFT_InboundFileGroups))
