private void CommonFormatLookup(FormControl _formControl)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(SKS_EFT_InboundFileFormat), _formControl);

    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundFileFormat, FormatID));
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundFileFormat, SKS_DD_Modules));

    queryBuildDataSource = query.addDataSource(tableNum(SKS_EFT_InboundFileFormat));

    queryBuildDataSource.clearRanges();
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundFileFormat, Selected));
    queryBuildRange.value(QueryValue(NoYes::Yes));

    if (element.args().menuItemName() == menuItemDisplayStr(SKS_EFT_APIInboundFileGroups))
    {
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundFileFormat, APIFormatType)).value(queryValue(SKS_EFT_APIFormatType::API));
    }
    else
    {
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundFileFormat, APIFormatType)).value(queryValue(SKS_EFT_APIFormatType::File));
    }

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

private boolean FormatIsSelected(str _FormatID)
{
    boolean retval = false;
    SKS_EFT_InboundFileFormat locSKS_EFT_InboundFileFormat;
    select firstOnly Recid from locSKS_EFT_InboundFileFormat
        where locSKS_EFT_InboundFileFormat.FormatID == _FormatID
            && locSKS_EFT_InboundFileFormat.Selected == NoYes::Yes;
    if(locSKS_EFT_InboundFileFormat)
    {
        retval = true;
    }
    return retval;
}

