public boolean validate()
{
    SKS_EFT_InboundFileGroups   lsks_eft_inboundfilegroups;
    boolean ret;

    ret = super();

    if(SKS_EFT_InboundFileCompletion.FileStatusAction == SKS_EB_FileStatusAction::MarkOutboundComplete)
    {
        select firstOnly autofilecompletion, inboundfilegroupid from lsks_eft_inboundfilegroups
            where lsks_eft_inboundfilegroups.InboundFileGroupId == SKS_EFT_InboundFileCompletion.InboundFileGroupId
                && lsks_eft_inboundfilegroups.AutoFileCompletion == SKS_EFT_FileCompletion::AllTransAccounted;

        if(lsks_eft_inboundfilegroups)
        {
            error(strFmt("@SKS2880","@SKS2878"));
            ret = false;
        }
    }

    return ret;
}

