public boolean validateDelete()
{
    boolean ret = true;
    SKS_EFT_FileFormat  lSKS_EFT_FileFormat;
    SKS_EFT_InboundFile lSKS_EFT_InboundFile;

    select firstOnly InboundFileGroupId from lSKS_EFT_FileFormat where lSKS_EFT_FileFormat.InboundFileGroupId == SKS_EFT_InboundFileGroups.InboundFileGroupId;
    select firstOnly InboundFileGroupId from lSKS_EFT_InboundFile where lSKS_EFT_InboundFile.InboundFileGroupId == SKS_EFT_InboundFileGroups.InboundFileGroupId;

    if(lSKS_EFT_FileFormat)
    {
        warning("@SKS2923");
        ret = false;
    }

    if(lSKS_EFT_InboundFile)
    {
        warning("@SKS2924");
        ret = false;
    }

    if(ret)
    {
        ret = super();
    }

    return ret;
}

