public void modified()
{
    SKS_EFT_InboundFileCompletion  lsks_eft_inboundfilecompletion;

    super();

    if(sks_eft_inboundfilegroups.AutoFileCompletion == SKS_EFT_FileCompletion::AllTransAccounted)
    {
        ttsBegin;

        while select forupdate filestatusaction from lsks_eft_inboundfilecompletion
            where lsks_eft_inboundfilecompletion.InboundFileGroupId == SKS_EFT_InboundFileGroups.InboundFileGroupId
        {
            lsks_eft_inboundfilecompletion.FileStatusAction = SKS_EB_FileStatusAction::LeaveOutboundUnchanged;
            lsks_eft_inboundfilecompletion.update();
        }

        ttsCommit;
        SKS_EFT_InboundFileCompletion_ds.research();
    }
}

public boolean validate()
{
    boolean ret;

    ret = super();
    SKS_EFT_InboundFileFormat inboundFileformat;

    if (ret && element.args().menuItemName() == menuItemDisplayStr(SKS_EFT_APIInboundFileGroups))
    {
        select firstonly RecId from inboundFileformat
            where inboundFileformat.FormatId == SKS_EFT_InboundFileGroups.InboundFormatId1
            && inboundFileformat.APIFormatType == SKS_EFT_APIFormatType::API;

        if (!inboundFileformat)
        {
            ret = checkFailed(strFmt("@SKS:SKGFileFormatIdCaanontUsed", SKS_EFT_InboundFileGroups.InboundFormatId1));
        }
    }
    else
    {
        select firstonly RecId from inboundFileformat
            where inboundFileformat.FormatId == SKS_EFT_InboundFileGroups.InboundFormatId1
            && inboundFileformat.APIFormatType == SKS_EFT_APIFormatType::File;

        if (!inboundFileformat)
        {
            ret = checkFailed(strFmt("@SKS:SKGAPIFormatIdCannotUsed", SKS_EFT_InboundFileGroups.InboundFormatId1));
        }
    }

    if(ret && SKS_EFT_InboundFileGroups.InboundFormatId1 != '')
    {    
        if(SKS_EFT_InboundFileGroups.InboundFormatId1 == SKS_EFT_InboundFileGroups.InboundFormatId2
            || SKS_EFT_InboundFileGroups.InboundFormatId1 == SKS_EFT_InboundFileGroups.InboundFormatId3)
        {
            error("@SKS2806");
            ret = false;
        }
        else if(!element.FormatIsSelected(SKS_EFT_InboundFileGroups.InboundFormatId1))
        {
            error("@SKS:SKGEFTFormatIsNotEnabled");
            ret = false;

        }
    }

    if(ret &&
        (
        (Substr(SKS_EFT_InboundFileGroups.InboundFormatId1, 1, 3)  == 'PSR' && subStr(SKS_EFT_InboundFileGroups.InboundFormatId1, strLen(SKS_EFT_InboundFileGroups.InboundFormatId1), -3) == '103')
        && ((Substr(SKS_EFT_InboundFileGroups.InboundFormatId2, 1, 3) == 'PSR' && subStr(SKS_EFT_InboundFileGroups.InboundFormatId2, strLen(SKS_EFT_InboundFileGroups.InboundFormatId2), -3) == '103')
        || (Substr(SKS_EFT_InboundFileGroups.InboundFormatId3, 1, 3) == 'PSR'  && subStr(SKS_EFT_InboundFileGroups.InboundFormatId3, strLen(SKS_EFT_InboundFileGroups.InboundFormatId3), -3) == '103'))
        )
        ||
        (
        (Substr(SKS_EFT_InboundFileGroups.InboundFormatId1, 1, 3)  == 'PSR' && subStr(SKS_EFT_InboundFileGroups.InboundFormatId1, strLen(SKS_EFT_InboundFileGroups.InboundFormatId1), -3) == '102')
        && ((Substr(SKS_EFT_InboundFileGroups.InboundFormatId2, 1, 3) == 'PSR' && subStr(SKS_EFT_InboundFileGroups.InboundFormatId2, strLen(SKS_EFT_InboundFileGroups.InboundFormatId2), -3) == '102')
        || (Substr(SKS_EFT_InboundFileGroups.InboundFormatId3, 1, 3) == 'PSR'  && subStr(SKS_EFT_InboundFileGroups.InboundFormatId3, strLen(SKS_EFT_InboundFileGroups.InboundFormatId3), -3) == '102'))
        )
      )
    {
        error("@SKS:SKGOnlyOnePSR");
        ret = false;
    }

    return ret;
}

public void modified()
{
    SKS_EFT_InboundFileCompletion   lSKS_EFT_InboundFileCompletion;
    SKS_EFT_InboundTransCompletion  lSKS_EFT_InboundTransCompletion;
    SKS_EFT_InboundStatuses         lSKS_EFT_InboundStatuses;
    super();

    while select recid, formatid from lSKS_EFT_InboundStatuses
        where lSKS_EFT_InboundStatuses.FormatId != SKS_EFT_InboundFileGroups.InboundFormatId1
            && lSKS_EFT_InboundStatuses.formatid != SKS_EFT_InboundFileGroups.InboundFormatID2
            && lSKS_EFT_InboundStatuses.formatid != SKS_EFT_InboundFileGroups.InboundFormatID3
    {
        delete_from lSKS_EFT_InboundFileCompletion
            where lSKS_EFT_InboundFileCompletion.InboundFileGroupId == SKS_EFT_InboundFileGroups.InboundFileGroupId
                && lSKS_EFT_InboundFileCompletion.InboundStatusRecID == lSKS_EFT_InboundStatuses.RecId;

        delete_from lSKS_EFT_InboundTransCompletion
            where lSKS_EFT_InboundTransCompletion.InboundFileGroupId == SKS_EFT_InboundFileGroups.InboundFileGroupId
                && lSKS_EFT_InboundTransCompletion.InboundStatusRecID == lSKS_EFT_InboundStatuses.RecId;

    }

    SKS_EFT_InboundFileCompletion_ds.research();
    SKS_EFT_InboundTransCompletion_ds.research();

}

