/// <summary>
/// Display form that is bind to.
/// </summary>
public void jumpRef()
{
    if (element.args().menuItemName() == menuItemDisplayStr(SKS_EFT_APIInboundFileGroups))
    {
        Args args;
        MenuFunction menuFunction;

        SKS_EFT_InboundFileFormat inboundFileFormat;

        select firstonly inboundFileFormat
            where inboundFileFormat.FormatId == this.valueStr();

        args = new Args();
        args.caller(element);
        args.record(inboundFileFormat);

        menuFunction = new MenuFunction(menuItemDisplayStr(SKS_EFT_APIInboundFileFormat), MenuItemType::Display);
        menuFunction.run(args);
    }
    else
    {
        super();
    }
}

