public Common lookupReference()
{
    SKS_EFT_InboundStatuses   inboundStatuses;
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this' is the current form control
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(SKS_EFT_InboundStatuses), ReferenceGroupFormat, true);
    ;

    // Add fields to be shown in the lookup form
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundStatuses, StatusID));
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundStatuses, StatusIdDisplay));


    // Limit and arrange the data selection
    queryBuildDataSource = query.addDataSource(tableNum(SKS_EFT_InboundStatuses));

    queryBuildDataSource.clearRanges();

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    queryBuildRange.value(queryValue(SKS_EFT_InboundFileGroups.InboundFormatId1));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    queryBuildRange.value(queryValue(SKS_EFT_InboundFileGroups.InboundFormatId2));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    queryBuildRange.value(queryValue(SKS_EFT_InboundFileGroups.InboundFormatId3));

    //queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    //queryBuildRange.value(queryValue(SKS_EFT_InboundStatusType::File));

    if (element.args().menuItemName() == menuItemDisplayStr(SKS_EFT_APIInboundFileGroups))
    {
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, StatusType)).value(queryValue(SKS_EFT_APIFormatType::API));
    }
    else
    {
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, StatusType)).value(queryValue(SKS_EFT_APIFormatType::File));
    }

    sysTableLookup.parmQuery(query);

    // Perform the lookup
    inboundStatuses = sysTableLookup.performFormLookup();

    return inboundStatuses;
}

public void jumpRef()
{
    Args args;
    MenuFunction menuFunction;

    args = new Args();
    args.caller(element);
    menuFunction = new MenuFunction(menuitemdisplaystr(SKS_EFT_InboundFileFormats), MenuItemType::Display);
    menuFunction.run(args);
}

public Common lookupReference()
{
    SKS_EFT_InboundStatuses   inboundStatuses;
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this' is the current form control
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(SKS_EFT_InboundStatuses), ReferenceGroup1, true);
    ;

    // Add fields to be shown in the lookup form
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundStatuses, StatusID));
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_InboundStatuses, StatusIdDisplay));


    // Limit and arrange the data selection
    queryBuildDataSource = query.addDataSource(tableNum(SKS_EFT_InboundStatuses));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    queryBuildRange.value(queryValue(SKS_EFT_InboundFileGroups.InboundFormatId1));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    queryBuildRange.value(queryValue(SKS_EFT_InboundFileGroups.InboundFormatId2));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    queryBuildRange.value(queryValue(SKS_EFT_InboundFileGroups.InboundFormatId3));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundStatuses, FormatID));
    queryBuildRange.value(queryValue(SKS_EFT_InboundStatusType::Transaction));

    sysTableLookup.parmQuery(query);

    // Perform the lookup
    inboundStatuses = sysTableLookup.performFormLookup();

    return inboundStatuses;
}
