    {
        tmpSKS_TmpVendBulkLoadTable.delete();
    }
}

void EnableDisablePrenote(Str _PaymSpec, Str _PaymMode)
{
    boolean PrenoteEnabled;
    ;

    If(_PaymSpec != '' && _PaymMode != '' )
    {
        PrenoteEnabled = SKS_EFT_VendorLoad::validatePreNote(_PaymMode,_PaymSpec);
        If(PrenoteEnabled)
        {
            SetPreNoteStatus.enabled(True);
        }
        else
        {
            SetPreNoteStatus.value(0);
            SetPreNoteStatus.enabled(False);
        }
    }

}

void FillComboBoxPaymMode()
{
    VendPaymModeTable bVendPaymModeTable;
    ;

    ComboBoxPaymMode.clear();

    While Select * from bVendPaymModeTable
          where bVendPaymModeTable.SKS_EFT_PaymMode == 1
    {
        ComboBoxPaymMode.add(bVendPaymModeTable.PaymMode);
    }
    ComboBoxPaymMode.selection(-1);
}

void FillComboBoxPaymSpec()
{
    VendPaymModeSpec bVendPaymModeSpec;
    ;

    ComboBoxPaymSpec.clear();
    While Select * from bVendPaymModeSpec
          where bVendPaymModeSpec.PaymMode == ComboBoxPaymMode.getText(ComboBoxPaymMode.selection())
    {
        ComboBoxPaymSpec.add(bVendPaymModeSpec.Specification);
    }
    ComboBoxPaymSpec.selection(0);

}

int64 getBankCount(VendAccount _accountNum)
{
    VendBankAccount vendbankacct;
    int64 CountRows;
    ;

    changecompany(companyid.text())
    {
        select count(RecId), AccountID, dataAreaId, VendAccount from vendbankacct where vendbankacct.VendAccount == _accountNum;

        CountRows = vendbankacct.RecId;
        bankCompanyId = vendbankacct.dataAreaId;
    }

    return countRows;
}

