void refreshParentForm()
{
    FormRun formRun;
    ;

    formRun = element.args().caller();

    //if the caller is a form then call a method on the caller form to refresh the datasource
    if (formRun)
    {
        if(strScan(formRun.toString(),'SKS_EFT_VendorDetails',0,strLen(formRun.toString()))>0)
        {
            element.args().caller().refreshForm();
        }
    }
}

void selectAll(boolean checked = true)
{
    Int64 lastRecId;
    SKS_EFT_FileFormatPaymSpec fileFormatPaymSpec;
    boolean isAutoCreateBankAccount;

    PaymMode paymMode = ComboBoxPaymMode.getText(ComboBoxPaymMode.selection());
    PaymSpec paymspec = ComboBoxPaymSpec.getText(ComboBoxPaymSpec.selection());

    SKS_TmpVendBulkLoadTable_DS.First();
    while(SKS_TmpVendBulkLoadTable)
    {
        if(paymMode && paymspec)
        {
            select firstonly fileFormatPaymSpec
                where fileFormatPaymSpec.FormatId == VendPaymModeTable::find(paymMode).SKS_EFT_FormatId
                    && fileFormatPaymSpec.PaymSpec == paymspec;

            if(fileFormatPaymSpec.APAutoCreateBankAccount)
            {
                isAutoCreateBankAccount = true;
            }
        }

        if(SKS_TmpVendBulkLoadTable.BankAccount != '')
        {
            SKS_TmpVendBulkLoadTable.Selected = true;
        }
        else
        {
            if(isAutoCreateBankAccount)
            {
                SKS_TmpVendBulkLoadTable.Selected = true;
            }
        }

        lastRecId = SKS_TmpVendBulkLoadTable.RecId;

        SKS_TmpVendBulkLoadTable_DS.Next();

        if(SKS_TmpVendBulkLoadTable.RecId == lastRecId)
        {
            break;
        }

    }
}

