public boolean validate()
{
    boolean ret;
    VendBankAccount vendBankAccount;
    ;
    changeCompany(CompanyID.valueStr())
    {
        select firstonly AccountId from vendBankAccount where
            vendBankAccount.VendAccount == SKS_TmpVendBulkLoadTable.AccountNum &&
            vendBankAccount.AccountID == SKS_TmpVendBulkLoadTable.BankAccount;

        if(vendBankAccount)
        {
            ret = true;
        }
        else
        {
            warning(strFmt("@SYS112224", SKS_TmpVendBulkLoadTable.BankAccount,"@SKS1796", "@SYS24216"));
            ret = false;
        }
    }
    return ret;
}

