void clicked()
{

    VendBankAccount vendbankacct;
    int64 CountRows;
    VendTable lVendTable;
    DirPartyTable  lDirPartyTable;
    RecordInsertList    ril;
    ;

    super();
    ril = new RecordInsertList(tableNum(SKS_TmpVendBulkLoadTable), false, false, false, false, false, SKS_TmpVendBulkLoadTable);

    element.deletegrid();


    ChangeCompany(Companyid.text())
    {
        while select AccountNum, DataAreaId, BankAccount, Party from lVendTable
            join Name from lDirPartyTable where lDirPartyTable.RecId == lVendTable.Party
        {
            //select Name from lDirPartyTable where lDirPartyTable.RecId == lVendTable.Party;
            CountRows = 0;
            SKS_TmpVendBulkLoadTable.AccountNum = lVendTable.AccountNum;
            SKS_TmpVendBulkLoadTable.CpnyID = lVendTable.dataAreaId;
            SKS_TmpVendBulkLoadTable.Name = lDirPartyTable.Name;

            CountRows = element.getBankCount(SKS_TmpVendBulkLoadTable.AccountNum);
            If (CountRows == 1)
            {
                select accountID from vendbankacct where vendbankacct.VendAccount == SKS_TmpVendBulkLoadTable.AccountNum;
                SKS_TmpVendBulkLoadTable.BankAccount = vendbankacct.AccountID;
            }
            If(CountRows > 1 )
            {
                If(lVendtable.BankAccount != '')
                {
                    select VendAccount, AccountID from vendbankacct where vendbankacct.VendAccount == SKS_TmpVendBulkLoadTable.AccountNum &&
                           vendbankacct.AccountID == lVendTable.BankAccount;
                    if(vendbankacct)
                    {
                        SKS_TmpVendBulkLoadTable.BankAccount = lVendtable.BankAccount;
                    }
                    else
                    {
                        SKS_TmpVendBulkLoadTable.BankAccount = '';
                    }
                }
                else
                {
                    SKS_TmpVendBulkLoadTable.BankAccount = '';
                }
            }
            If(CountRows == 0 )
            {
                SKS_TmpVendBulkLoadTable.BankAccount = '';
            }

            SKS_TmpVendBulkLoadTable.VBACpnyID = bankCompanyId;
            ril.add(SKS_TmpVendBulkLoadTable);
        }
        ril.insertdatabase();
    }

    SKS_TmpVendBulkLoadTable_ds.executeQuery();


}

void clicked()
{

    DialogButton diagBut;
    VendTable mvendTable;
    SKS_EFT_Setup local_SKS_EFT_Setup;
    Boolean Vendor_create, PaymentSpec_create;
    int Counter;
    ;

    super();

    if(ComboBoxPaymMode.valueStr() == '' && ComboBoxPaymSpec.valueStr() == '' && CreatePaySpec.checked())
    {
        warning("@SKS1804","@SKS1785");
        return;
    }
    If(Selectedcnt > 0)
    {
        diagBut = Box::yesNo("@SKS1798",DialogButton::No, "@SKS1785");

        if (diagBut == DialogButton::Yes)
        {
            counter = 0;

            while select * from SKS_TmpVendBulkLoadTable where SKS_TmpVendBulkLoadTable.Selected == NoYes::Yes
            {
                PaymentSpec_create = false;
                Vendor_create = false;

                local_SKS_EFT_Setup = SKS_EFT_Setup::find();

                changecompany(Companyid.text())
                {
                    select forupdate mvendtable where mvendtable.AccountNum == SKS_TmpVendBulkLoadTable.AccountNum;
                }
                Vendor_create = SKS_EFT_VendorLoad::CreateVendorRecord(mvendtable, local_SKS_EFT_Setup, SetActive.checked(), EnableInPayJournal.checked(), UseEmail.checked(), Companyid.text());

                VendTable locVendTable = VendTable::find(SKS_TmpVendBulkLoadTable.AccountNum);
                SKS_EFT_FileFormatPaymSpec fileFormatPaymSpec;

                select firstonly fileFormatPaymSpec
                        where fileFormatPaymSpec.FormatId == VendPaymModeTable::find(ComboBoxPaymMode.valueStr()).SKS_EFT_FormatId
                            && fileFormatPaymSpec.PaymSpec == ComboBoxPaymSpec.valueStr();

                // if vendor bank exists

                VendBankAccount vendBankAccount;

                If(ComboBoxPaymMode.valueStr() != '' && ComboBoxPaymSpec.valueStr() != '' && CreatePaySpec.checked() && SKS_TmpVendBulkLoadTable.BankAccount)
                {
                    PaymentSpec_create = SKS_EFT_VendorLoad::CreateVendorPaymRecord(mvendtable,local_SKS_EFT_Setup,ComboBoxPaymMode.valueStr(),ComboBoxPaymSpec.valueStr(),
                                                           SKS_TmpVendBulkLoadTable.BankAccount,CompBankAcct.valueStr(), SetPreNoteStatus.checked(), Companyid.text());
                }
                // if vendor bank account does not exists
                else
                {
                    If(ComboBoxPaymMode.valueStr() != '' && ComboBoxPaymSpec.valueStr() != '' && CreatePaySpec.checked() && !SKS_TmpVendBulkLoadTable.BankAccount && fileFormatPaymSpec.APAutoCreateBankAccount)
                    {
                        //Create a new TAS_AUTO bank for the vendor

                        vendBankAccount = SKS_EFT_APCreateBankAccount::createVendBankAccountBulkLoad(locVendTable);

                        PaymentSpec_create = SKS_EFT_VendorLoad::CreateVendorPaymRecord(mvendtable,local_SKS_EFT_Setup,ComboBoxPaymMode.valueStr(),ComboBoxPaymSpec.valueStr(),
                                                           vendBankAccount.AccountID,CompBankAcct.valueStr(), SetPreNoteStatus.checked(), Companyid.text());
                    }
                }

                If(SetMethod.checked())
                {
                    ttsbegin;
                    changecompany(Companyid.text())
                    {
                        mvendTable.PaymMode = ComboBoxPaymMode.valueStr();
                        mvendTable.PaymSpec = ComboBoxPaymSpec.valueStr();

                        if(SKS_TmpVendBulkLoadTable.BankAccount)
                        {
                            mvendTable.BankAccount = SKS_TmpVendBulkLoadTable.BankAccount;
                        }
                        else
                        {
                            mvendTable.BankAccount = vendBankAccount.AccountID;    //update the vendor bank account TAS_AUTO
                        }

                        mvendTable.update();
                    }
                    ttscommit;
                }
                IF(Vendor_create || PaymentSpec_create)
                {
                    counter++;
                }

            }
            SKS_TmpVendBulkLoadTable_ds.executeQuery();
            info(strfmt("@SKS1805",int2str(counter)));

            if (counter > 0)
            {
                element.refreshParentForm();
            }
        }
    }
    else
    {
        warning("@SKS1812","@SKS1785");
    }

}

