[Form]
public class SKS_EFT_VendorDetails extends FormRun
{
    SKS_EFT_Setup SKS_EFT_Setup;
}

void defaultBankTemplateItems()
{

    if (SKS_EB_Vendor.EMTemplateFromBank == NoYes::Yes)
    {
        SKS_EB_Vendor.EMTemplateNoAttach = '';
    }
    else
    {
        SKS_EB_Vendor.EMTemplateNoAttach = sks_eft_setup.APEMTemplateNoAttach;

        if(SKS_EB_Vendor.EMTemplateLanguage == '')
        {
            SKS_EB_Vendor.EMTemplateLanguage = sks_eft_setup.APEMTemplateLanguage;
        }
    }
    //SKS_bug_9856 start
    if (SKS_EB_Vendor.EMTemplateWithAttachFromBank == NoYes::Yes)
    {
        SKS_EB_Vendor.EMTemplateAttach = '';
    }
    else
    {
        SKS_EB_Vendor.EMTemplateAttach = sks_eft_setup.APEMTemplateAttach;

        if(SKS_EB_Vendor.EMTemplateLanguage == '')
        {
            SKS_EB_Vendor.EMTemplateLanguage = sks_eft_setup.APEMTemplateLanguage;
        }
    }
    //sks_bug_9856 start

    if (SKS_EB_Vendor.EMTemplateFromBank == NoYes::Yes && SKS_EB_Vendor.EMTemplateWithAttachFromBank == NoYes::Yes)
    {
        SKS_EB_Vendor.EMTemplateLanguage = '';
    }

    element.setEnableBankTemplateItems();
}

void disableEmailTab()
{
    boolean enabled;
    ;

    if(SKS_EFT_Setup.ModAPEmail == NoYes::No)
    {
        enabled = false;
    }
    else
    {
        enabled = true;
    }
    SKS_EB_Vendor_EMNotification.enabled(enabled);
    SKS_EB_Vendor_EMReplyToFromBank.enabled(enabled);
    SKS_EB_Vendor_EMTemplateFromBank.enabled(enabled);
    SKS_EB_Vendor_EMTemplateFromSetup.enabled(enabled);

}

void enableButtonsActive(boolean _enable)
{
    ;

    APPaymentSpec.enabled(_enable);

}

public void init()
{
    SKS_PP_Sys tmpSKS_PP_Sys;
    ;

    super();
    tmpSKS_PP_Sys = new SKS_PP_Sys();

    if ( !tmpSKS_PP_Sys.ValidateSetup(this.name()))
    {
        this.close();
    }
    else
    {

        select * from sks_eft_setup;
    }

    AttachmentMessage.text("@SKS1501");

    if(element.args().record())
    {
        SKS_EB_Vendor sks_EB_VendorLoc = element.args().record();
        if(sks_EB_VendorLoc)
        {
            SKS_EB_Vendor_ds.query().dataSourceTable(tableNum(SKS_EB_Vendor)).addRange(fieldNum(SKS_EB_Vendor, VendAccount)).value(sks_EB_VendorLoc.vendAccount);
            SKS_EB_Vendor_ds.query().dataSourceTable(tableNum(SKS_EB_Vendor)).addRange(fieldNum(SKS_EB_Vendor, VendCompany)).value(sks_EB_VendorLoc.vendCompany);
        }
    }
}

