public void setEmailActive()
{
    SKS_EB_Vendor.EMNotification = SKS_EFT_Setup.ModAPEmail;
    SKS_EB_Vendor.EM1Vendor = NoYes::No;

    if(SKS_EFT_VendorLoad::VendorAddressPrimaryContactPurposeInUse() || SKS_EFT_VendorLoad::PrimaryVendorEmailExists(SKS_EB_Vendor.VendCompany, SKS_EB_Vendor.VendAccount))
    {
        SKS_EB_Vendor.EM1Vendor = NoYes::Yes;
        SKS_EB_Vendor_EM1Vendor.vendorEM1_chk();
    }
}

void setEnableBankTemplateItems()
{
    SKS_EB_Vendor_EMTemplateNoAttach.enabled(!SKS_EB_Vendor.EMTemplateFromBank);
    SKS_EB_Vendor_EMTemplateLanguage.enabled(!SKS_EB_Vendor.EMTemplateFromBank);
    SKS_EB_Vendor_EMTemplateAttach.enabled(!SKS_EB_Vendor.EMTemplateWithAttachFromBank);
    SKS_EB_Vendor_EMTemplateAttachLanguage.enabled(!SKS_EB_Vendor.EMTemplateWithAttachFromBank);
}

void setEnableReplyTo()
{
    boolean enabledProperty;
    ;

    if (SKS_EB_Vendor.EMReplyToFromBank == NoYes::Yes)
    {
        enabledProperty = boolean::false;
        sks_EB_Vendor.EMReplyToAddr = '';
        sks_EB_Vendor.EMReplyToName = '';
    }
    else
    {
        enabledProperty = boolean::true;
        SKS_EB_Vendor.EMReplyToName = SKS_EFT_Setup.APEMReplytoName;
        SKS_EB_Vendor.EMReplyToAddr = SKS_EFT_Setup.APEMReplyToAddr;
    }
    SKS_EB_Vendor_EMReplyToName.enabled(enabledProperty);
    SKS_EB_Vendor_EMReplyToAddr.enabled(enabledProperty);

}

