public void ViewHideControls()
{
    //Hide if Vendor Address Primary Contact is in the SKS_EFT_VendoPurpose table (TAS Params > AP EFT+/DirectDebit > AP EFT+ Setup > EMail notifictions tab
    EM1Name.visible(!SKS_EFT_VendorLoad::VendorAddressPrimaryContactPurposeInUse());
    EM1Addr.visible(!SKS_EFT_VendorLoad::VendorAddressPrimaryContactPurposeInUse());

    //Display the message directing users to the Payment spec form when Vendor Address Primary Contact is in the SKS_EFT_VendoPurpose table (TAS Params > AP EFT+/DirectDebit > AP EFT+ Setup > EMail notifictions tab
    EmailInformationOnPaymSpecFormMsg.visible(SKS_EFT_VendorLoad::VendorAddressPrimaryContactPurposeInUse());
    EmailInformationOnPaymSpecFormMsg.text("@SKS6302");
}

