public void create(boolean _append = false)
{
    super(_append);

    SKS_EB_Vendor.EMTemplateFromSetup = NoYes::Yes;
    SKS_EB_Vendor.Active = NoYes::Yes;
    SKS_EB_Vendor.PaymCanChange = NoYes::Yes;
    SKS_EB_Vendor.EMReplyToFromBank = NoYes::Yes;
    SKS_EB_Vendor.EMTemplateFromBank = NoYes::Yes;
    SKS_EB_Vendor.EMTemplateWithAttachFromBank = NoYes::Yes;

    element.loadAPEFTEmailActiveFromSetup();
    element.loadAPEFTFromSetup();
    element.validateAPEFTSetupDefaults();
    element.setEnableReplyTo();
    element.setEnableBankTemplateItems();

    //SKS_9496_CR start
    SKS_EB_Vendor.APTemplateFromSetup = NoYes::Yes;
    SKS_EB_Vendor.APSRS1EMTemplateFromSetup = NoYes::Yes;
    SKS_EB_Vendor.APSRS2EMTemplateFromSetup = NoYes::Yes;
    element.loadSKS_AP_TemplateFromSetup();
    element.loadAPSRS1EMTemplateFromSetupDefaults();
    element.loadAPSRS2EMTemplateFromSetup();
    //SKS_9496_CR end
}

public int active()
{
    int ret;
    ;

    element.disableEmailTab();
    element.validateAPEFTSetupDefaults();
    element.setEnableBankTemplateItems();
    element.setEnableReplyTo();
    SKS_EB_Vendor_EM1Vendor.vendorEM1_chk();
    element.ViewHideControls();
    element.enableButtonsActive(true);

    //SKS_9496_CR start
    element.validateSKS_AP_SRS1EMTemplateFromSetupDefaults();
    element.validateSKS_AP_SRS2EMTemplateFromSetupDefaults();
    element.validateSKS_AP_TemplateFromSetupDefaults();

    //SKS_9496_CR end

    ret = super();

    return ret;
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validateWrite()
{
    boolean ret;
    FormDesign lFormDesign = this.formRun().design();

    if (!SKS_EFT_VendorLoad::VendorAddressPrimaryContactPurposeInUse() &&
        SKS_EB_Vendor.EMNotification
        && EM1Addr.text() == ''
        && SKS_EB_Vendor.EM2Addr == ''
        && SKS_EB_Vendor.EM3Addr == ''
        && SKS_EB_Vendor.EM4Addr == '')
    {
        ret = false;
        error("@SKS:SKGEmailRequired");
    }
    else
    {
        ret = super();
    }

    if (ret)
    {
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EB_Vendor.EMTemplateNoAttach, SKS_EB_Vendor.EMTemplateLanguage) && ret;
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EB_Vendor.EMTemplateAttach, SKS_EB_Vendor.EMTemplateLanguage) && ret;
    }

    return ret;
}

