/// <summary>
/// Gets all SSRS reports for SRS1 remit attachment detail with 'SKS_AP' prefix.
/// </summary>
// SKS-FDD-BS-AP remit attach - 9496
public void lookup()
{
    #ResAppl
    Object  formRun;
    container names;
    DictClass dictClass;
    List listSpecifications;
    ListEnumerator listEnumerator;
    ClassId sks_ddr_adviceRootClassId = classNum(SKS_APR_AttachAdviceController);

    formRun = classFactory.createSysLookupPicklist();
    formRun.init();

    listSpecifications = new DictClass(sks_ddr_adviceRootClassId).extendedBy();

    listEnumerator = listSpecifications.getEnumerator();
    while (listEnumerator.moveNext())
    {
        dictClass = new DictClass(listEnumerator.current());

        if (dictClass && !dictClass.isAbstract())
        {
            names += dictClass.name();
        }
    }

    formRun.choices(names, #ImageTable);
    formRun.caption("@SYS60851");
    this.performFormLookup(formRun);
}

/// <summary>
///Handles modification of ap template from setup checkbox for attachment 2
/// </summary>
/// <returns>boolean</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if(SKS_EB_Vendor.APSRS1EmailSSRS != 'SKS_APR_RemitAttachControllerV2')
        {
            APSRS1AttachmentDetail_APHideDueDateAtt1.enabled(false);
            SKS_EB_Vendor.APHideDueDateAtt1 = false;
        }
        else
        {
            APSRS1AttachmentDetail_APHideDueDateAtt1.enabled(true);
        }
    }

    return ret;
}

