public void bulkUpdate(SKS_EFT_PaymentSpecificationOption _specOption
                    , str _paymSpec
                    , SKS_EFT_PostingDateOption _dateOption
                    , date _dateValue
                    , date _newDate)
{
    int locCounter = 0;
    Voucher prevVoucher;
    RefRecId lastRecId;
    FormDataObject transDateField = LedgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans,TransDate));

    LedgerJournalTrans_ds.first();

    while(LedgerJournalTrans.RecId != 0)
    {
        if((_specOption == SKS_EFT_PaymentSpecificationOption::Specific
                && LedgerJournalTrans.PaymSpec != _paymSpec)
            || (_dateOption == SKS_EFT_PostingDateOption::Specific
                && LedgerJournalTrans.TransDate != _dateValue)
            || (prevVoucher == LedgerJournalTrans.Voucher))
        {
            lastRecid = LedgerJournalTrans.RecId;

            LedgerJournalTrans_ds.next();

            if(LedgerJournalTrans.RecId == lastRecid)
            {
                break;
            }

            continue;
        }

        //trasdatefield.modified modifies all transactions with the same voucher number
        // skip same voucher to prevent update errors
        prevVoucher = LedgerJournalTrans.Voucher;

        LedgerJournalTrans.TransDate = _newDate;
        int cursorPos = LedgerJournalTrans_ds.getPosition();
        transDateField.modified();

        ttsBegin;
        LedgerJournalTrans.update();
        ttsCommit;

        locCounter++;

        //modified event will move the cursor so we need to reset it
        LedgerJournalTrans_ds.setPosition(cursorPos);

        lastRecid = LedgerJournalTrans.RecId;

        LedgerJournalTrans_ds.next();

        if(LedgerJournalTrans.RecId == lastRecid)
        {
            break;
        }
    }


    if(locCounter)
    {
        info(strFmt("@SKS3877", int2str(locCounter)));
    }
    else
    {
        warning(strFmt("@SKS3877", int2str(locCounter)));
    }
}

public boolean validateWrite()
{
    boolean ret;
    LedgerJournalTrans.SKS_EFT_AllowTransDateModifications = NoYes::Yes;
    ret = super();
    return ret;
}

public int active()
{
    int ret;
    ret = super();

    if(gSKS_EFTAP_File.EFTFileType == SKS_EFT_Module::BTB)
    {
        Select gSKS_BTB_PaymModeTable
            where gSKS_BTB_PaymModeTable.PaymMode == LedgerJournalTrans.SKS_BTB_TransferMethod
            join gSKS_EFT_FileFormat
                where gSKS_EFT_FileFormat.FormatId == gSKS_BTB_PaymModeTable.FormatId
            join gSKS_EFT_FileFormatPaymSpec
                where gSKS_EFT_FileFormatPaymSpec.FormatId == gSKS_EFT_FileFormat.FormatId
                    && gSKS_EFT_FileFormatPaymSpec.PaymSpec == LedgerJournalTrans.SKS_BTB_TransferSpec;
    }
    else
    {
        Select gVendPaymModeTable
            where gVendPaymModeTable.PaymMode == LedgerJournalTrans.PaymMode
            join gSKS_EFT_FileFormat
                where gSKS_EFT_FileFormat.FormatId == gVendPaymModeTable.SKS_EFT_FormatId
            join gSKS_EFT_FileFormatPaymSpec
                where gSKS_EFT_FileFormatPaymSpec.FormatId == gSKS_EFT_FileFormat.FormatId
                    && gSKS_EFT_FileFormatPaymSpec.PaymSpec == LedgerJournalTrans.PaymSpec;
    }

    gSKS_EFT_FilePmtSelection = gParentForm.getSKS_EFT_FilePmtSelection();
    gSKS_EFTAP_File = gParentForm.getSKS_EFTAP_File();
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,TransDate)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,Company)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,LedgerDimension)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,Txt)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,AmountCurDebit)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,AmountCurCredit)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,OffsetLedgerDimension)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,PaymentStatus)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,JournalNum)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,PaymMode)).allowEdit(false);
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,PaymSpec)).allowEdit(false);
    //EffectiveDate.enabled(false);
    select * from gLedgerJournalTable where gLedgerJournalTable.JournalNum == LedgerJournalTrans.JournalNum;
    boolean enable = false;
    if(gSKS_EFTAP_File.EFTFileType == SKS_EFT_Module::AP || gSKS_EFTAP_File.EFTFileType == SKS_EFT_Module::BTB || gSKS_EFTAP_File.EFTFileType == SKS_EFT_Module::CNP)
    {
        enable = true;
    }
    LedgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans,TransDate))
        .allowEdit(!gLedgerJournalTable.Posted && enable);
    btnMultipleDateChange.enabled(enable);
    //EffectiveDate.visible(gSKS_EFT_FileFormat.EffectiveDateMethod == SKS_EFT_EffectiveDateMethod::PaymentSpecOffset && gSKS_EFTAP_File.EFTFileType == SKS_EFT_Module::AP);
    return ret;
}

public void executeQuery()
{
    QueryBuildRange qbrRange;
    QueryBuildDataSource qbdSource;

    this.query().dataSourceTable(tablenum(ledgerjournaltrans)).clearRanges();
    qbdSource = this.query().DataSourceTable(tablenum(ledgerjournaltrans));

    if(gSKS_EFT_FilePmtSelection.Module == SKS_EFT_Module::AP)
    {
        qbrRange = qbdsource.addRange(fieldnum(ledgerjournaltrans, SKS_EFT_APPmtSelectionRecId));
    }
    else if(gSKS_EFT_FilePmtSelection.Module == SKS_EFT_Module::BTB)
    {
        qbrRange = qbdsource.addRange(fieldnum(ledgerjournaltrans, SKS_EFT_BTBPmtSelectionRecId));
    }
    else if(gSKS_EFT_FilePmtSelection.Module == SKS_EFT_Module::CNP)
    {
        qbrRange = qbdsource.addRange(fieldnum(ledgerjournaltrans, SKS_EFT_CNPPmtSelectionRecId));
    }
    else
    {
        qbrRange = qbdsource.addRange(fieldnum(ledgerjournaltrans, SKS_EFT_ARPmtSelectionRecId));
    }

    qbrRange.value(QueryValue(gSKS_EFT_FilePmtSelection.RecId));

    super();
}

