/// <summary>
///Added JumpRef method because standared hyperlink is not working properly
/// </summary>
public void jumpRef()
{
    Args args = new Args();
    if(gSKS_EFTAP_File.EFTFileType == SKS_EFT_Module::BTB)
    {
        args.record(LedgerJournalTable::find(gSKS_EFT_FilePmtSelection.PmtJournalNum));

        new MenuFunction(MenuItemDisplayStr(SKS_BTB_BankTransferJournal),MenuItemType::Display).run(Args);

    }
    else if(gSKS_EFTAP_File.EFTFileType == SKS_EFT_Module::CNP)
    {
        args.record(LedgerJournalTable::find(gSKS_EFT_FilePmtSelection.PmtJournalNum));

        new MenuFunction(MenuItemDisplayStr(SKS_CNP_CustReimbursementJour),MenuItemType::Display).run(Args);

    }
    else
    {
        args.record(LedgerJournalTable::find(gSKS_EFT_FilePmtSelection.PmtJournalNum));
        FormRun formRun = new MenuFunction(menuitemDisplayStr(LedgerJournalTable), MenuItemType::Display).create(args);
        formRun.run();
        formRun.wait();
    }
}

/// <summary>
///
/// </summary>
public void modified()
{
    LedgerJournalEngine engine;
    container balances;
    LedgerJournalTable journal;
    Voucher origVoucher = LedgerJournalTrans.Voucher;
    TransDate origDate = LedgerJournalTrans.TransDate;
    RefRecId lastRecid;

    engine = new LedgerJournalEngine_VendPayment(element);
    engine.newJournalActive(LedgerJournalTrans.ledgerJournalTable());

    super();

    LedgerJournalTrans_ds.first();

    while(LedgerJournalTrans.RecId != 0)
    {
        if(origVoucher == LedgerJournalTrans.Voucher)
        {
            //After updating a transaction that shares a voucher number with other transactions,
            // those transactions will also have their date updated but the exchange rate is not
            // so reread the row as to not cause an update conflict.
            LedgerJournalTrans_ds.reread();
            LedgerJournalTrans.TransDate = origDate;
            engine.transDateModified(LedgerJournalTrans);
            engine.updatePaymentFee(LedgerJournalTrans, CustVendPaymJournalFee);
        }

        lastRecid = LedgerJournalTrans.RecId;

        LedgerJournalTrans_ds.next();

        if(LedgerJournalTrans.RecId == lastRecid)
        {
            break;
        }
    }

    LedgerJournalTrans_ds.research(true);
    LedgerJournalTrans_ds.refresh();

    balances = engine.journalBalanceMST();

    select firstonly forupdate journal where journal.JournalNum == LedgerJournalTrans.JournalNum;

    journal.JournalTotalDebit = conPeek(balances, 1);
    journal.JournalTotalCredit = conPeek(balances, 2);
    journal.JournalTotalOffsetBalance = conPeek(balances, 3);

    ttsBegin;
    journal.update();
    ttsCommit;
}

