void setLBControlProperties()
{
    SKS_EB_BankIntegOptWithNoLicense bankIntOption = SKS_EB_FileStorage::find().StorageOption_LB;        
    boolean visibleLBPath = false;
    boolean visibleLBBankFabricLocation = false;

    if(bankIntOption == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        visibleLBBankFabricLocation = true;            
    }
    else
    {
        visibleLBPath = true;        
    }

    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,LBPath)).visible(visibleLBPath);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,LBBankFabricLocation)).visible(visibleLBBankFabricLocation);  

    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,LBArchivePath)).visible(visibleLBPath);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,LBArchive)).visible(visibleLBPath);
}

public void EnableDisableAutomationControls()
{
