    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,LBInboundCommId)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,LBAutoDefaultCust)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,LBAutoIgnoreErr)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,LBAutoPmtJrnl)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,LBPmtPost)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,LBAutoCreateDepSlip)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,MatchingRulesGroupID)).enabled(!SKS_LB_Bank.LBFromSetup);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,MoveGoodPaymToBatch)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,PaymMustBeZero)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,NoPartialSettle)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,AutoJournalName)).enabled(!SKS_LB_Bank.LBAutoFromSetup);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,LBRestrictCrossCompanySearch)).enabled(!SKS_LB_Bank.LBFromSetup);
