    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,AutoSubmitWF)).enabled(!SKS_LB_Bank.LBFromSetup);
    SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank,LBRestrictCrossCompanySearch)).enabled(!SKS_LB_Bank.LBFromSetup);
    SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,LBBankFabricLocation)).enabled(!SKS_LB_Bank.LBFromSetup);

    if(!SKS_LB_Bank.LBFromSetup)
    {
        if(SKS_LB_Bank.LBArchive == NoYes::No)
        {
            SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,LBArchivePath)).enabled(SKS_LB_Bank.LBArchive);                   
        }
    }
    if(SKS_LB_Bank.AutoJournalName == '')
    {
        SKS_LB_Bank.AutoJournalName = SKS_LB_Bank.LBJournalName;
    }
   // sks_lb_bank_ds.research();

    element.EnableDisableAutomationControls();

    element.setLBControlProperties();

    return ret;
}

