public boolean validateWrite()
{
    boolean ret;
    SKS_LB_Import               cls_SKS_LB_Import  = new SKS_LB_Import();
    System.Text.StringBuilder   sbDuplicateWarning = new System.Text.StringBuilder();
    System.Text.StringBuilder   sbFolderCreateWarning = new System.Text.StringBuilder();
    SKS_LB_Bank     local_sks_lb_bank;

    ret = super();

    if(!SKS_LB_Bank_LBJournalName.valueStr() || !LBFormatId_Overview.valueStr())
    {
        //Box::warning("@SKS253","@SKS179"); //required field
        warning(strFmt("@SKS2643",strFmt('%1 & %2', "@SKS112", "@SKS428")));                

        SKS_LB_BANK_LBJournalName.setFocus();

        return false;
    }

    if(SKS_LB_Bank.LBArchive && !SKS_LB_Bank.LBArchivePath)
    {
        warning("@SKS1657" + ' ' + strFmt("@SKS2643","@SKS75")); //required field

        return false;
    }

    if(SKS_LB_Bank.MatchingRulesGroupId == '')
    {
        Box::warning("@SKS253","@SKS179"); //required field

        SKS_LB_Bank_MatchingRulesGroupId.setFocus();

        return false;
    }

    if ( gChangedLBPath == true && SKS_LB_Bank.LBFromSetup == false)
    {
        // Create the In Process folder.
        if ( cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::Create, SKS_LB_Bank.LBPath, SKS_LB_Bank.LBAutoServerSide) != '')
        {
            sbFolderCreateWarning.Append("@SKS3172");
            sbFolderCreateWarning.Replace('%1', cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::InProcess_FolderName, SKS_LB_Bank.LBPath, SKS_LB_Bank.LBAutoServerSide));
            sbFolderCreateWarning.Replace('%2', cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::Complete_FolderName, SKS_LB_Bank.LBPath, SKS_LB_Bank.LBAutoServerSide));
            sbFolderCreateWarning.Replace('%2', cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::Errors_FolderName, SKS_LB_Bank.LBPath, SKS_LB_Bank.LBAutoServerSide));
            sbFolderCreateWarning.Replace('%4', SKS_LB_Bank.LBPath);

            return false;
        }
        gChangedLBPath = false;
    }

    //Do not allow duplicate LBPath/LBBankFabricLocation values.  Each BankAcct must have a unique LockBox Path
    if( SKS_LB_Bank.LBFormatId != '')
    {
        SKS_EB_BankIntegOptWithNoLicense bankIntOption = SKS_EB_FileStorage::find().StorageOption_LB;
        if(bankIntOption == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            // See if others bank accts are using this path
            select firstonly BankAccountId, LBFromSetup, LBFormatId, LBBankFabricLocation from local_sks_lb_bank
            // don't get this bank account record and only get those with a lockbox format id
            where local_sks_lb_bank.LBFormatId != ''  &&
                //make sure it doesn't find itself
                local_sks_lb_bank.recid != SKS_LB_Bank.recid &&
                // if this one is using the path in setup
                ((SKS_LB_Bank.LBFromSetup &&
                    // then look for others using the path in setup
                    (local_sks_lb_bank.LBFromSetup == true ||
                    // or others not using the path in setup and their path matches the path in setup
                    (local_sks_lb_bank.LBFromSetup == false && local_sks_lb_bank.LBBankFabricLocation == sks_pp_setup.LBBankFabricLocation))) ||
                // or if this one is NOT using setups path
                (!SKS_LB_Bank.LBFromSetup &&
                    // then look for others not using setups path and their path matches this ones path
                    (local_sks_lb_bank.LBFromSetup == false && local_sks_lb_bank.LBBankFabricLocation == SKS_LB_Bank.LBBankFabricLocation) ||
                    // or other using setups path and setups path matches this ones path
                    (local_sks_lb_bank.LBFromSetup == true && sks_pp_setup.LBBankFabricLocation == SKS_LB_Bank.LBBankFabricLocation)));
        }
        else
        {
            // See if others bank accts are using this path
            select firstonly BankAccountId, LBFromSetup, LBFormatId, LBPath from local_sks_lb_bank
            // don't get this bank account record and only get those with a lockbox format id
            where local_sks_lb_bank.LBFormatId != ''  &&
                //make sure it doesn't find itself
                local_sks_lb_bank.recid != SKS_LB_Bank.recid &&
                // if this one is using the path in setup
                ((SKS_LB_Bank.LBFromSetup &&
                    // then look for others using the path in setup
                    (local_sks_lb_bank.LBFromSetup == true ||
                    // or others not using the path in setup and their path matches the path in setup
                    (local_sks_lb_bank.LBFromSetup == false && local_sks_lb_bank.LBPath == sks_pp_setup.LBPath))) ||
                // or if this one is NOT using setups path
                (!SKS_LB_Bank.LBFromSetup &&
                    // then look for others not using setups path and their path matches this ones path
                    (local_sks_lb_bank.LBFromSetup == false && local_sks_lb_bank.LBPath == SKS_LB_Bank.LBPath) ||
                    // or other using setups path and setups path matches this ones path
                    (local_sks_lb_bank.LBFromSetup == true && sks_pp_setup.LBPath == SKS_LB_Bank.LBPath)));
        }

        if (local_sks_lb_bank)    // We found some accounts sharing this ones path
        {
            sbDuplicateWarning.Append("@SKS969");  // "The Path to lockbox files for Bank Accounts must be unique."
            sbDuplicateWarning.Append(' '); // a space

            // This one is trying to use the path in setup
            if (SKS_LB_Bank.LBFromSetup)
            {
                // The one found is using the path in setup
                if (local_sks_lb_bank.LBFromSetup)
                {
                    // Already an account using the path in setup
                    // Bank Account %1 cannot be set to use the path from eBanking Suite Setup because Bank Account %2 is already set to use the path from eBanking Suite Setup.
                    sbDuplicateWarning.Append("@SKS971");
                }
                else    // The one found is not using the path in setup
                {
                    // The setup path matches another bank acct path
                    // Bank Account %1 cannot be set to use the path from eBanking Suite Setup because the path from eBanking Suite Setup matches the path from Bank Account %2.
                    sbDuplicateWarning.Append("@SKS972");
                }
            }
            else    // This one is not trying to use the path in setup
            {
                // The one found is using the path in setup
                if (local_sks_lb_bank.LBFromSetup)
                {
                    // This path matches the path in setup
                    // The path for Bank Account %1 cannot match the path from eBanking Suite Setup because Bank Account %2 is set to use the path from eBanking Suite Setup.
                    sbDuplicateWarning.Append("@SKS973");
                }
                else    // The one found is not using the path in setup
                {
                    // two bank acct are trying to use the same path
                    // The path for Bank Account %1 cannot match the path from Bank Account %2.
                    sbDuplicateWarning.Append("@SKS492");
                }
            }

            sbDuplicateWarning.Replace('%1', SKS_LB_Bank.BankAccountId);
            sbDuplicateWarning.Replace('%2', local_sks_lb_bank.BankAccountId);
            sbDuplicateWarning.Replace('%3', local_sks_lb_bank.LBFormatId);
            sbDuplicateWarning.Replace('%4', SKS_LB_Bank.LBFormatId);
            box::warning(sbDuplicateWarning.ToString());

            if(bankIntOption == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
            {
                SKS_LB_Bank_LBBankFabricLocation.setFocus();
            }
            else
            {
                SKS_LB_Bank_LBPath.setFocus();
            }

            return false;
        }
    }

    return ret;
}

/// <summary>
/// Validates that workflow has been turned on
/// </summary>
///   /// //<returns>
/// a boolean value
/// </returns>
public boolean validateWorkFlowJournal()
{
    boolean ret = true;
    LedgerJournalName  lLedgerJournalName;

    if(SKS_lb_bank.AutoSubmitWF == NoYes::Yes && ret)
    {
        select firstonly lLedgerJournalName where lLedgerJournalName.journalname == SKS_lb_bank.LBJournalName;
        if(lLedgerJournalName.WorkFlowApproval == NoYes::No)
        {
            Error(strFmt("@SKS:SKGWFNotEnabled",  SKS_lb_bank.LBJournalName));
            ret = false;
        }
    }
    return ret;
}

/// <summary>
///Displays journal names
/// </summary>
/// <param name = "_formControl">
/// name of form control
/// </param>
/// <param name = "_filterStr">
///filter variable 
///</param>
public void lookup(FormControl _formControl, str _filterStr)
{

    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup =
        SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, JournalName));
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, Name));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));
    queryBuildRange = queryBuildDataSource.addRange(
        fieldNum(LedgerJournalName, JournalType));
    queryBuildRange.value('customer payment');
    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();

}

