public boolean modified()
{
    boolean ret;

    ret = super();

    if(SKS_LB_Bank.LBAutoPmtJrnl == NoYes::No)
    {
        SKS_LB_Bank.MoveGoodPaymToBatch = NoYes::No;
        SKS_LB_Bank.NoPartialsettle = NoYes::No;
        SKS_LB_Bank.PaymMustBeZero = NoYes::No;
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,MoveGoodPaymToBatch)).enabled(false);
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,PaymMustBeZero)).enabled(false);
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,NoPartialSettle)).enabled(false);
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank, AutoJournalName)).enabled(false);

        if(!SKS_LB_Bank.LBPmtPost)
        {
            SKS_LB_Bank.LBAutoCreateDepSlip = NoYes::No;
            SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank, LBAutoCreateDepSlip)).enabled(false);
        }
    }
    else
    {
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,MoveGoodPaymToBatch)).enabled(true);
        SKS_LB_Bank_ds.object(fieldnum(SKS_LB_Bank, LBAutoCreateDepSlip)).enabled(true);
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank, AutoJournalName)).enabled(true);
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if(SKS_LB_Bank.MoveGoodPaymToBatch == NoYes::No)
    {
        SKS_LB_Bank.PaymMustBeZero = NoYes::No;
        SKS_LB_Bank.NoPartialsettle = NoYes::No;
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,PaymMustBeZero)).enabled(false);
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,NoPartialSettle)).enabled(false);
    }
    else
    {
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,PaymMustBeZero)).enabled(true);
        SKS_LB_Bank_ds.object(fieldNum(SKS_LB_Bank,NoPartialSettle)).enabled(true);
    }

    return ret;
}
