void defaultBankTemplateItems()
{

    if (SKS_LB_Depositor.EMTemplateFromBank == NoYes::Yes)
    {
        SKS_LB_Depositor.EMTemplateNoAttach = '';
        SKS_LB_Depositor.EMAENTemplateNoAttach = '';
    }
    else
    {
        SKS_LB_Depositor.EMTemplateNoAttach = sks_eft_setup.AREMTemplateNoAttach;
        SKS_LB_Depositor.EMAENTemplateNoAttach = sks_eft_setup.ARAENTemplateNoAttach;

        if(SKS_LB_Depositor.EMTemplateLanguage == '')
        {
            SKS_LB_Depositor.EMTemplateLanguage = sks_eft_setup.AREMTemplateLanguage;
        }
    }
    // SKS_Bug_9586 -->
    if (SKS_LB_Depositor.EMTemplateWithAttachFromBank == NoYes::Yes)
    {
        SKS_LB_Depositor.EMTemplateAttach = '';
        SKS_LB_Depositor.EMAENTemplateAttach = '';
    }
    else
    {
        SKS_LB_Depositor.EMTemplateAttach    = sks_eft_setup.AREMTemplateAttach;
        SKS_LB_Depositor.EMAENTemplateAttach = sks_eft_setup.ARAENTemplateAttach;

        if(SKS_LB_Depositor.EMTemplateLanguage == '')
        {
            SKS_LB_Depositor.EMTemplateLanguage = sks_eft_setup.AREMTemplateLanguage;
        }
    }

    if (SKS_LB_Depositor.EMTemplateWithAttachFromBank == NoYes::Yes
        && SKS_LB_Depositor.EMTemplateFromBank == NoYes::Yes)
    {
        SKS_LB_Depositor.EMTemplateLanguage = '';
    }
    // SKS_Bug_9586 <--
    element.setEnableBankTemplateItems();
}

