void enableDisableExternalSettlement()
{
    if(SKS_LB_Depositor.LBSettlementMethod == SKS_LB_SettlementMethod::External)
    {
        SKS_LB_Depositor_LBExternalFormatId.enabled(true);
        SKS_LB_Depositor_LBExternalPath.enabled(true);
        SKS_LB_Depositor_LBBankFabricExtLocation.enabled(true);

        if( SKS_LB_Depositor.LBExternalFormatId == '')
        {
            SKS_LB_Depositor.LBExternalFormatId = SKS_PP_Setup::find().LBExternalFormatId;
        }
        if(SKS_LB_Depositor.LBExternalPath == '')
        {
            SKS_LB_Depositor.LBExternalPath = SKS_PP_Setup::find().LBExternalPath;
        }
        if(SKS_LB_Depositor.LBBankFabricExtLocation == '')
        {
            SKS_LB_Depositor.LBBankFabricExtLocation = SKS_PP_Setup::find().LBBankFabricExtLocation;
        }
    }
    else
    {
        SKS_LB_Depositor_LBExternalFormatId.enabled(false);
        SKS_LB_Depositor.LBExternalFormatId = '';

        SKS_LB_Depositor_LBExternalPath.enabled(false);
        SKS_LB_Depositor.LBExternalPath = '';

        SKS_LB_Depositor_LBBankFabricExtLocation.enabled(false);
        SKS_LB_Depositor.LBBankFabricExtLocation = '';
    }
}

public void init()
{
    SKS_PP_Sys tmpSKS_PP_Sys;
    ;

    super();
    tmpSKS_PP_Sys = new SKS_PP_Sys();

    if ( !tmpSKS_PP_Sys.ValidateSetup(this.name()))
    {
        this.close();
    }
    else
    {
        select firstonly * from sks_eft_setup;
        select firstonly * from m_SKS_PP_Setup;
    }

    SKS_LB_Depositor_DS.object(fieldNum(SKS_LB_Depositor,LBExternalPath)).visible(SKS_EB_FileStorage::find().StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage::find().StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);
    SKS_LB_Depositor_DS.object(fieldNum(SKS_LB_Depositor,LBBankFabricExtLocation)).visible(SKS_EB_FileStorage::find().StorageOption_LB== SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);

}

