void setEnableBankTemplateItems()
{
    boolean enabledProperty;
    ;

    if (SKS_LB_Depositor.EMTemplateFromBank == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }
    SKS_LB_Depositor_EMTemplateNoAttach.enabled(enabledProperty);
    SKS_LB_Depositor_EMTemplateAttach.enabled(!SKS_LB_Depositor.EMTemplateWithAttachFromBank); // SKS_Bug_9586
    SKS_LB_Depositor_EMTemplateLanguage.enabled(enabledProperty);
    SKS_LB_Depositor_EMAENTemplateAttach.enabled(!SKS_LB_Depositor.EMTemplateWithAttachFromBank); // SKS_Bug_9586
    SKS_LB_Depositor_EMAENTemplateNoAttach.enabled(enabledProperty);
    SKS_LB_Depositor_EMTemplateAttachLanguage.enabled(!SKS_LB_Depositor.EMTemplateWithAttachFromBank);

}

