public void create(boolean _append = false)
{
    ;
    super(_append);

    SKS_LB_Depositor.LBSettlementMethod = m_SKS_PP_Setup.LBSettlementMethod;

    SKS_LB_Depositor.EMTemplateFromSetup = NoYes::Yes;
    SKS_LB_Depositor.Active = NoYes::Yes;
    SKS_LB_Depositor.PaymCanChange = NoYes::Yes;
    SKS_LB_Depositor.EMReplyToFromBank = NoYes::Yes;
    SKS_LB_Depositor.EMTemplateFromBank = NoYes::Yes;
    SKS_LB_Depositor.EMTemplateWithAttachFromBank = NoYes::Yes; // SKS_Bug_9586

    element.loadAREFTEmailActiveFromSetup();
    element.loadAREFTFromSetup();
    element.validateAREFTSetupDefaults();
    element.setEnableReplyTo();
    element.setEnableBankTemplateItems();

    SKS_LB_Depositor.AutoCRFromSetup = NoYes::Yes;
    // FDD-BS-DD remit attach - 7369CR -->
    SKS_LB_Depositor.SKS_DDR_TemplateFromSetup = NoYes::Yes;
    SKS_LB_Depositor.SKS_DDR_SRS2EMTemplateFromSetup = NoYes::Yes;
    SKS_LB_Depositor.SKS_DDR_SRS1EMTemplateFromSetup = NoYes::Yes;
    element.loadSKS_DDR_TemplateFromSetup();
    element.loadSRS1EMTemplateFromSetupDefaults();
    element.loadSRS2EMTemplateFromSetup();
    // FDD-BS-DD remit attach - 7369CR <--
    element.loadAutoCreditFromSetup();
}

public boolean validateWrite()
{
    boolean ret = true;
    if(SKS_LB_Depositor.LBSettlementMethod == SKS_LB_SettlementMethod::External)
    {
        if(!SKS_LB_Depositor.LBExternalFormatId)
        {
            error(strFmt("@SKS1580",SKS_LB_Depositor_LBExternalFormatId.labelText()));
            SKS_LB_Depositor_LBExternalFormatId.setFocus();
            ret = false;
        }

        if(SKS_EB_FileStorage::find().StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_LB_Depositor.LBBankFabricExtLocation == '' && SKS_PP_Setup::find().LBBankFabricExtLocation == '')
            {
                error(strFmt("@SKS1580",  SKS_LB_Depositor_LBExternalPath.labelText()));
                SKS_LB_Depositor_LBExternalPath.setFocus();
                ret = false;
            }
        }
        else
        {
            if(SKS_LB_Depositor.LBExternalPath == '' && SKS_PP_Setup::find().LBExternalPath == '')
            {
                error(strFmt("@SKS1580",  SKS_LB_Depositor_LBExternalPath.labelText()));
                SKS_LB_Depositor_LBExternalPath.setFocus();
                ret = false;
            }
        }
    }

    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_LB_Depositor.EMTemplateNoAttach, SKS_LB_Depositor.EMTemplateLanguage) && ret;
    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_LB_Depositor.EMTemplateAttach, SKS_LB_Depositor.EMTemplateLanguage) && ret;
    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_LB_Depositor.EMAENTemplateNoAttach, SKS_LB_Depositor.EMTemplateLanguage) && ret;
    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_LB_Depositor.EMAENTemplateAttach, SKS_LB_Depositor.EMTemplateLanguage) && ret;

    if (ret)
    {
        ret = super();
    }

    return ret;
}

public int active()
{
    int ret;
    ;

    element.validateAREFTSetupDefaults();
    element.setEnableBankTemplateItems();
    // FDD-BS-DD remit attach - 7369CR -->
    element.validateSKS_DDR_SRS1EMTemplateFromSetupDefaults();
    element.validateSKS_DDR_SRS2EMTemplateFromSetupDefaults();
    element.validateSKS_DDR_TemplateFromSetupDefaults();
    // FDD-BS-DD remit attach - 7369CR <--
    element.setEnableReplyTo();
    SKS_LB_Depositor_EM1Customer.customerEM1_chk();

    if(m_SKS_LB_Setup_AutoCRParms.CustomerCompany != SKS_LB_Depositor.CustomerCompany)
    {
        m_SKS_LB_Setup_AutoCRParms = SKS_LB_Setup_AutoCRParms::find(SKS_LB_Depositor.CustomerCompany);
    }

    if(SKS_LB_Depositor.AutoCRFromSetup == NoYes::Yes)
    {
        AutoCreditRefreshFromSetup.clicked();
        SKS_LB_Depositor_DS.object(fieldNum(SKS_LB_Depositor, writeoffid)).allowedit(false);
    }
    else
    {
        SKS_LB_Depositor_DS.object(fieldNum(SKS_LB_Depositor, writeoffid)).allowedit(true);
    }

    element.enableButtonsActive(true);

    element.changeFreeTextLedgerCompany(sks_LB_Depositor.CustomerCompany ? sks_LB_Depositor.CustomerCompany : curext());
    element.changeGLLedgerCompany(sks_LB_Depositor.CustomerCompany ? sks_LB_Depositor.CustomerCompany : curext());

    ret = super();

    element.enableDisableExternalSettlement();

    SKS_LB_Depositor_DS.object(fieldNum(SKS_LB_Depositor,LBExternalPath)).visible(SKS_EB_FileStorage::find().StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage::find().StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);
    SKS_LB_Depositor_DS.object(fieldNum(SKS_LB_Depositor,LBBankFabricExtLocation)).visible(SKS_EB_FileStorage::find().StorageOption_LB== SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);

    return ret;
}

