public boolean validate()
{
    boolean ret;
    SKS_LB_FileFormatDet lLBFormatDet;
    ;

    ret = super();
    Select count(FieldStart) from lLBFormatDet
        where lLBFormatDet.FormatID == SKS_LB_Depositor.LBExternalFormatId &&
            (lLBFormatDet.FieldType == SKS_LB_FieldType::InvNumber ||
            lLBFormatDet.FieldType == SKS_LB_FieldType::InvAmt);

    if (lLBFormatDet.FieldStart != 2)
    {
        //Lockbox file formats used for settlements must have invoice number and invoice amount fields mapped
        ret = Global::checkFailed("@SKS910");
    }

    Select count(FieldStart) from lLBFormatDet
        where lLBFormatDet.FormatID == SKS_LB_Depositor.LBExternalFormatId &&
        lLBFormatDet.FieldType == SKS_LB_FieldType::CheckNumber;
    if (lLBFormatDet.FieldStart < 1 && ret)
    {
         //Lockbox file formats used for settlements should have a check number(it is not required, just recommended)
         //@SKS941:  Check number is not mapped in the selected format.\nCheck Number validation cannot be performed on External Settlement import.
         //@SKS883:  Customer eBanking details
        Box::info(strFmtLB(strfmt("@SKS941")),"@SKS883");
    }


    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        ret = SKS_EB_FileHub::directoryExistsForm(SKS_LB_Depositor.LBExternalPath);
    }

    return ret;
}

