    Formrun formRun;
    ;
    //depositorCompany = [SKS_LB_Depositor.CustomerCompany];
    //select crossCompany:depositorCompany * from customertable;
    changeCompany(SKS_LB_Depositor.CustomerCompany)
    {

        select * from customertable where customertable.AccountNum == SKS_LB_Depositor.Accountnum;
        args = new Args();
        args.record(customertable);

        args.name(formstr(CustBankAccounts));
        args.caller(this);
        formRun = classFactory.formRunClass( Args );

        formRun.run();
        formRun.wait(false);
    }
    super();
}

