public void lookup()
{

    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup =
        SysTableLookup::newParameters(tableNum(SKS_LB_FileFormat), this);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(SKS_LB_FileFormat, FormatID));
    sysTableLookup.addLookupField(fieldNum(SKS_LB_FileFormat, Descr));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(SKS_LB_FileFormat));
    queryBuildRange = queryBuildDataSource.addRange(
        fieldNum(SKS_LB_FileFormat, FileType));
    queryBuildRange.value(enum2str(sks_LB_FileType::CSV)+','+enum2str(sks_LB_FileType::FixedField)+','+enum2str(sks_LB_FileType::TabDelimited));
    sysTableLookup.parmQuery(query);
    // Perform the lookup.
    sysTableLookup.performFormLookup();

    //Don't call super(), this should take its place.
    //   super()
}

/// <summary>
/// lookup method
/// </summary>
public void lookup()
{            
    if(SKS_LB_Depositor.LBSettlementMethod == SKS_LB_SettlementMethod::External)
    {
        Query query = new Query();
        QueryBuildDataSource qbds;
        QueryBuildRange queryBuildRange;
        SysTableLookup sysTableLookup;

        sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),this);
        sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Name));
        sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));
        qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));
        queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable,Name));
        sysTableLookup.parmTmpBuffer(SKS_BF_TmpLookupTable::construct(SKS_BF_FileLocCategory::ARESExt));
        sysTableLookup.performFormLookup();
    }
}

public void clicked()
{
    super();

    this.customerEM1_chk();
}

void customerEM1_chk()
{
    CustTable                       customerTable;
    DirPartyContactInfoview         mDirPartyContactInfoview;
    CustName                        custname;
    // 7536 -->
    LogisticsElectronicAddress      logisticsElectronicAddress;
    DirPartyLocation                dirPartyLocation;
    LogisticsLocationRole           locationRole;
    LogisticsElectronicAddressRole  logisticsElectronicAddressRole;
    // 7536 <--

    if(SKS_LB_Depositor_EM1Customer.value())
    {
        if(SKS_LB_Depositor.CustomerCompany)
        {
            SKS_EFT_Setup = SKS_EFT_Setup::find();
            customerTable = CustTable::findByCompany(SKS_LB_Depositor.CustomerCompany, SKS_LB_Depositor.AccountNum);
            if(SKS_EFT_Setup.EmailContactPurpose != '')
            {
                // 7536 -->
                select firstonly Locator, Description from logisticsElectronicAddress
                    where logisticsElectronicAddress.Type == LogisticsElectronicAddressMethodType::Email
                exists join dirPartyLocation
                    where dirPartyLocation.Location == logisticsElectronicAddress.Location &&
                            dirPartyLocation.Party == customerTable.Party
                exists join logisticsElectronicAddressRole
                    where logisticsElectronicAddressRole.ElectronicAddress == logisticsElectronicAddress.RecId
                exists join locationRole
                   where locationRole.RecId == logisticsElectronicAddressRole.LocationRole
                        && locationRole.Name == SKS_EFT_Setup.EmailContactPurpose;

                EM1Name.text(logisticsElectronicAddress.Description);
                EM1Addr.text(logisticsElectronicAddress.Locator);
                // 7536 <--
            }
            else
            {
                select firstonly * from mDirPartyContactInfoview
                     where mDirPartyContactInfoview.Party == customerTable.Party && mDirPartyContactInfoview.IsPrimary == true
                           &&  mDirPartyContactInfoview.Type == LogisticsElectronicAddressMethodType::Email;
                custname = mDirPartyContactInfoView.LocationName;
                EM1Name.text(custname);
                EM1Addr.text(mDirPartyContactInfoView.Locator);
            }
        }
    }
    else
    {
        EM1Name.text('');
        EM1Addr.text('');
    }
}

