/// <summary>
///  Gets all SSRS reports for SRS1 remit attachment detail with 'SKS_DDRA' prefix.
/// </summary>
// FDD-BS-DD remit attach - 7369CR
[SuppressBPWarning('BPUpgradeCodeLateBoundCall', 'Required')]
public void lookup()
{

    #ResAppl
    Object  formRun;
    container names;
    DictClass dictClass;
    List listSpecifications;
    ListEnumerator listEnumerator;
    ClassId sks_ddra_adviceRootClassId = classNum(SKS_DDRA_AttachAdviceController);

    formRun = classFactory.createSysLookupPicklist();
    formRun.init();

    listSpecifications = new DictClass(sks_ddra_adviceRootClassId).extendedBy();

    listEnumerator = listSpecifications.getEnumerator();
    while (listEnumerator.moveNext())
    {
        dictClass = new DictClass(listEnumerator.current());

        if (dictClass && !dictClass.isAbstract())
        {
            names += dictClass.name();
        }
    }

    formRun.choices(names, #ImageTable);
    formRun.caption("@SYS60851");
    this.performFormLookup(formRun);
}

