public void init()
{
    SKS_PP_Sys   SKS_PP_Sys;

    super();

    SKS_PP_Sys = new SKS_PP_Sys();

    if ( !SKS_PP_Sys.ValidateSetup(this.name()))
    {
        this.close();
    }
}

void setEnableDisable()
{

    If (SKS_LB_FileFormat.FormatID == '')
    {
        BtnFieldLayout.enabled(false);
    }
    else
    {
        BtnFieldLayout.enabled(true);
        switch (SKS_LB_FileFormat.BatchSplitType)
        {
            case SKS_LB_BatchSplitType::SplitByIdentifier:
                SKS_LB_FileFormat.BatchPaymentCount = 0;
                SKS_LB_FileFormat_BatchPaymentCount.enabled(false);
                break;

            case SKS_LB_BatchSplitType::SplitByCount:
                SKS_LB_FileFormat_BatchPaymentCount.enabled(true);
                break;

            case SKS_LB_BatchSplitType::None:
                SKS_LB_FileFormat.BatchPaymentCount = 0;
                SKS_LB_FileFormat_BatchPaymentCount.enabled(false);
                break;
        }

        if(SKS_LB_FileFormat.DecimalSeparator != '')
        {
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecPl)).allowedit(false);
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecimalSeparator)).allowedit(true);
            SKS_LB_FileFormat.ImpliedDecs = NoYes::No;
        }
        else
        {
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecPl)).allowedit(true);
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecimalSeparator)).allowedit(false);
            SKS_LB_FileFormat.ImpliedDecs = NoYes::yes;
        }

        switch (SKS_LB_FileFormat.FileType)
        {
            case SKS_LB_FileType::CSV:
                SKS_LB_FileFormat_OmitHeaderRecs.enabled(True);
                SKS_LB_FileFormat_OmitTrailerRecs.enabled(True);
                SKS_LB_FileFormat_PmtAmtEQSumChkAmts.enabled(True);
                SKS_LB_FileFormat_EDISeperator.enabled(false);
                SKS_LB_FileFormat_EDITerminator.enabled(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, invcredindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, negpaymindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, SettleAmtIncludesAdjAmt)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CombineADXRefNbr)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMTChkNumType)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMT54PaymDate)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, Delimiter)).allowEdit(true);

                SKS_LB_FileFormat_LineEndingType.visible(true);
                break;

            case SKS_LB_FileType::TabDelimited:
                SKS_LB_FileFormat_OmitHeaderRecs.enabled(true);
                SKS_LB_FileFormat_OmitTrailerRecs.enabled(True);
                SKS_LB_FileFormat_PmtAmtEQSumChkAmts.enabled(true);
                SKS_LB_FileFormat_EDISeperator.enabled(false);
                SKS_LB_FileFormat_EDITerminator.enabled(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, invcredindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, negpaymindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, SettleAmtIncludesAdjAmt)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CombineADXRefNbr)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMTChkNumType)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMT54PaymDate)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, Delimiter)).allowEdit(false);

                SKS_LB_FileFormat_LineEndingType.visible(true);
                break;

            case SKS_LB_FileType::FixedField:
                SKS_LB_FileFormat_OmitHeaderRecs.enabled(True);
                SKS_LB_FileFormat_OmitTrailerRecs.enabled(True);
                SKS_LB_FileFormat_PmtAmtEQSumChkAmts.enabled(True);
                SKS_LB_FileFormat_EDISeperator.enabled(false);
                SKS_LB_FileFormat_EDITerminator.enabled(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, invcredindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, negpaymindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, SettleAmtIncludesAdjAmt)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CombineADXRefNbr)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMTChkNumType)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMT54PaymDate)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, Delimiter)).allowEdit(false);

                SKS_LB_FileFormat_LineEndingType.visible(true);
                break;

            case SKS_LB_FileType::EDI820:
            case SKS_LB_FileType::EDI823:
                SKS_LB_FileFormat.PmtAmtEQSumChkAmts = 0;
                SKS_LB_FileFormat_OmitHeaderRecs.enabled(false);
                SKS_LB_FileFormat_OmitTrailerRecs.enabled(false);
                SKS_LB_FileFormat_PmtAmtEQSumChkAmts.enabled(false);
                SKS_LB_FileFormat_EDISeperator.enabled(true);
                SKS_LB_FileFormat_EDITerminator.enabled(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, invcredindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, negpaymindic)).allowedit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, SettleAmtIncludesAdjAmt)).allowEdit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CombineADXRefNbr)).allowEdit(true);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMTChkNumType)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMT54PaymDate)).allowEdit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, Delimiter)).allowEdit(false);

                SKS_LB_FileFormat_LineEndingType.visible(false);
                break;

            case SKS_LB_FileType::CAMT54V1:
            case SKS_LB_FileType::CAMT54V2:
                SKS_LB_FileFormat.PmtAmtEQSumChkAmts = 0;
                SKS_LB_FileFormat_OmitHeaderRecs.enabled(false);
                SKS_LB_FileFormat_OmitTrailerRecs.enabled(false);
                SKS_LB_FileFormat_PmtAmtEQSumChkAmts.enabled(false);
                SKS_LB_FileFormat_EDISeperator.enabled(false);
                SKS_LB_FileFormat_EDITerminator.enabled(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, invcredindic)).allowedit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, negpaymindic)).allowedit(false);
                SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, SettleAmtIncludesAdjAmt)).allowEdit(false);
