                if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::CAMT54V1)
                {
                    SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, CAMTChkNumType)).allowEdit(true);
