public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if((SKS_LB_FileFormat.FileType == SKS_LB_FileType::EDI820) || (SKS_LB_FileFormat.FileType == SKS_LB_FileType::EDI823))
    {
        if(SKS_LB_FileFormat.EDISeperator == '')
        {
            error(strFmt("@SKS2468", "@SKS2975"));
            ret = false;
        }
    }

    if(SKS_LB_FileFormat.ImpliedDecs == NoYes::No &&  SKS_LB_FileFormat.DecimalSeparator != '.' && SKS_LB_FileFormat.DecimalSeparator != ',')
    {
        error("@SKS3266");
        ret = false;
    }

    return ret;
}

public void write()
{
    SKS_LB_FileFormatDet tmpsks_lb_fileformatdet;
    ;

    super();

    //if record 6 and record 4 does exist, create them
    if(SKS_LB_FileFormat.FileType == SKS_LB_Filetype::BAI2)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.FormatID == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            ttsbegin;
            // Create 6 record mappings
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 1, 1, SKS_LB_FieldType::RecordType, SKS_LB_FormatDetType::MultiLineCheck);
            //Create Batch and Sequence for Check
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 2, 4, SKS_LB_FieldType::BatchNumber, SKS_LB_FormatDetType::MultiLineCheck);
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 5, 7, SKS_LB_FieldType::BatchSequence, SKS_LB_FormatDetType::MultiLineCheck);

            //Create record 4 record mappings
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 1, 1, SKS_LB_FieldType::RecordType, SKS_LB_FormatDetType::MultiLineInvoice);
            //Create Batch and Sequence for Invoice
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 2,4, SKS_LB_FieldType::BatchNumber, SKS_LB_FormatDetType::MultiLineInvoice);
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 5,7, SKS_LB_FieldType::BatchSequence, SKS_LB_FormatDetType::MultiLineInvoice);
            //Create LastIndicator fieldtype
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 11, 11, SKS_LB_FieldType::LastIndicator, SKS_LB_FormatDetType::MultiLineInvoice);

            //5 record (Batch header)
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 15, 20, SKS_LB_FieldType::HeadDateYYMMDD, SKS_LB_FormatDetType::BatHeader);
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 8, 14, SKS_LB_FieldType::LBBankAccount, SKS_LB_FormatDetType::BatHeader);

            //7 record (Batch trailer)
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 21, 24, SKS_LB_FieldType::BankPmtCnt, SKS_LB_FormatDetType::BatTrailer);
            SKS_LB_CreateBAI2::Insert_BAI2(SKS_LB_FileFormat.FormatID, 25, 35, SKS_LB_FieldType::BankPmtTotal, SKS_LB_FormatDetType::BatTrailer);
            ttsCommit;
        }
    }
    else if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::EDI820)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.formatid == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            ttsBegin;
            SKS_LB_CreateEDI::Create_EDI820(SKS_LB_FileFormat.FormatID);
            ttsCommit;
        }
    }
    else if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::EDI823)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.formatid == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            SKS_LB_CreateEDI::Create_EDI823(SKS_LB_FileFormat.FormatID);
        }
    }
    else if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::Symcor)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.formatid == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            SKS_LB_CreateSymcor::Create_Symcor(SKS_LB_FileFormat.FormatID);
        }
    }
    else if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::WFRMF)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.formatid == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            SKS_LB_CreateWFRMF::Create_WFRMF(SKS_LB_FileFormat.FormatID);
        }
    }
    else if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::WFARRemitFile)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.formatid == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            SKS_LB_CreateWFARRMF::Create_WFARRMF(SKS_LB_FileFormat.FormatID);
        }
    }
    else if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::Colombia)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.formatid == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            SKS_LB_CreateColombia::Create_Colombia(SKS_LB_FileFormat.FormatID);
        }
    }
    else if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::IntReceive)
    {
        select firstonly * from tmpsks_lb_fileformatdet where tmpsks_lb_fileformatdet.formatid == SKS_LB_FileFormat.FormatID;

        if(!tmpsks_lb_fileformatdet)
        {
            ttsBegin;
            SKS_LB_CreateIntegratedReceivables::Create_IR(SKS_LB_FileFormat.FormatID);
            ttsCommit;
        }
    }
}

public boolean validateDelete()
{
    boolean ret = true;
    ;
    //Are you sure you  want to delete this Payment line and any associated settlement information?
    //The only way to retrieve this record once deleted is to re-import
    //the entire lockbox transmission file containing this record.
    if (Box::yesNo("@SKS2644",DialogButton::No, "@SKS352")==DialogButton::Yes)
    {

        ret = true;
    }
    else
    {
        ret=false;
    }
    return ret;

}

public int active()
{
    int ret;

    element.setEnableDisable();

    ret = super();

    return ret;
}

public boolean validate()
{
    SKS_PP_Setup    sks_pp_setup;
    boolean ret;
    ret = super();
    if(ret && SKS_LB_FileFormat.CombineADXRefNbr)
    {
        select firstonly fftpm from sks_pp_setup;
        if(!sks_pp_setup.fftpm)
        {
            error("@SKS3920");
            ret = false;
        }
    }
    return ret;
}

public boolean validate()
{
    boolean ret;
    int thisval;
    System.Text.StringBuilder sb = new System.Text.StringBuilder();

    ;

    thisval = str2int(SKS_LB_FileFormat_DecPl.valueStr());

    if(thisval < 0 )
    {
        sb.Append("@SKS239");
        sb.Replace('%1','0');
        Box::stop(sb.ToString(),"@SKS225"); //must be greater than 0

        SKS_LB_FileFormat_DecPl.setFocus();

        return false;
    }
    else if(thisval > 6 )
    {
        sb.Append("@SKS240");
        sb.Replace('%1','6');
        Box::stop(sb.ToString(),"@SKS225"); //must be less than 6

        SKS_LB_FileFormat_DecPl.setFocus();

        return false;
    }

    ret = super();


    return ret;

}

public void modified()
{

    SKS_LB_FileFormatDet   l_SKS_LB_FileFormatDet;
    ;

    element.setEnableDisable();

    If (SKS_LB_FileFormat.orig().FormatID  != SKS_LB_FileFormat.FormatID)
    {
        ttsbegin;
        Update_recordset l_SKS_LB_FileFormatDet setting FormatID = SKS_LB_FileFormat.FormatID
        where l_SKS_LB_FileFormatDet.FormatID == SKS_LB_FileFormat.orig().FormatID;
        ttscommit;

    }

    super();
}

