public boolean validate()
{
    SKS_LB_FileFormatDet    SKS_LB_FileFormatDet;
    boolean ret;
    ;

    ret = super();

    if(ret && (SKS_LB_FileFormat.FileType != SKS_LB_FileType::BAI2
        && SKS_LB_FileFormat.FileType != SKS_LB_Filetype::Symcor
        && SKS_LB_FileFormat.FileType != SKS_LB_FileType::CAMT54V1
        && SKS_LB_FileFormat.FileType != SKS_LB_FileType::CAMT54V2
        && SKS_LB_FileFormat.FileType != SKS_LB_FileType::WFRMF))
    {
        if(SKS_LB_FileFormat.VerifyAcct == NoYes::yes)
        {
            select firstOnly fieldtype from SKS_LB_FileFormatDet where SKS_LB_FileFormatDet.formatid == SKS_LB_FileFormat.formatid
                && SKS_LB_FileFormatDet.fieldtype == SKS_LB_FieldType::LBBankAccount;

            if(!SKS_LB_FileFormatDet)
            {
                error("@SKS2653");
                ret = false;
            }
        }
    }

    if(SKS_LB_FileFormat.FileType == SKS_LB_FileType::EDI820 || SKS_LB_FileFormat.FileType == SKS_LB_FileType::EDI823)
    {
        select SKS_LB_FileFormatDet
            where SKS_LB_FileFormatDet.FormatID ==  SKS_LB_FileFormat.FormatID
                &&  SKS_LB_FileFormatDet.EDISegment != SKS_LB_EDISegment::ST
                &&  SKS_LB_FileFormatDet.FieldType ==  SKS_LB_FieldType::LBBankAccount;
        if(SKS_LB_FileFormatDet)
        {
            Error("@SKS:SKSBatchHeaderErr");
            ret = false;
        }
    }
    else
    {
        select SKS_LB_FileFormatDet
            where SKS_LB_FileFormatDet.FormatID ==  SKS_LB_FileFormat.FormatID
                &&  SKS_LB_FileFormatDet.FormatDetType != SKS_LB_FormatDetType::BatHeader
                &&  SKS_LB_FileFormatDet.FieldType ==  SKS_LB_FieldType::LBBankAccount;
        if(SKS_LB_FileFormatDet)
        {
            Error("@SKS:SKSBatchHeaderErr");
            ret = false;
        }
    }

    return ret;
}

/// <summary>
/// implied decimals modified methods
/// </summary>
/// <returns>boolean</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if(SKS_LB_FileFormat.ImpliedDecs == NoYes::Yes)
        {
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecPl)).allowedit(true);
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecPl)).enabled(true);
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecimalSeparator)).allowedit(false);
            SKS_LB_FileFormat.DecimalSeparator = '';
            //SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecimalSeparator)).setValue('');
        }
        else
        {
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecPl)).allowedit(false);
            SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecimalSeparator)).allowedit(true);
            SKS_LB_FileFormat.DecPl = 0;
            //SKS_LB_FileFormat_ds.object(fieldNum(SKS_LB_FileFormat, DecPl)).setValue('');
        }
    }

    return ret;
}
