[Form]
public class SKS_PP_Setup extends FormRun
{
    TmpIdRef                tmpIdRef;
    boolean                 runExecuteDirect;
    boolean                 gChangedLBPath;
    NumberSeqScope             scope;
    NumberSeqApplicationModule numberSeqApplicationModule;
    container                  numberSequenceModules;
    boolean activationCompleted;
    RefRecId currentCOARecId;
    SelectableDataArea currentLedgerCompany;
    #define.ValidAnnualPeriod2(11)
    #define.ValidAnnualPeriod1(-1)


}

public void activate(boolean _active)
{


    SKS_PP_Setup_LBArchive.archivecheckbox_chk();

    SKS_EFT_Setup_ModAPEFT.apEFTcheckbox_chk();

    SKS_EFT_Setup_ModAREFT.arEFTcheckbox_chk();

    SKS_PP_Setup_LBPurgeFiles.enableRetention();

    SKS_PP_Setup_PPPurgeFiles.enableRetention();

    super(_active);

    if((!SKS_PP_Setup.RegUnlockKey) && (!activationCompleted))
    {
        Registration.setFocus();
        activationCompleted = true;
    }

    select firstonly SKS_EB_FileStorage;
    element.setBRControlProperties();
    element.setCCRControlProperties();
    element.setLBControlProperties();
    element.setPositivePayTab();
    element.setIHBControlProperties();
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    sks_eft_setup_ds.research(true);

    if(SKS_EFT_Setup.ModAPEFT == NoYes::Yes)
    {

        if(!SKS_EFT_Setup_ACHImmDest.valueStr())
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1160", "@SKS1028"));
            SKS_EFT_Setup_ACHImmDest.setFocus();
            return false;
        }

        if(!SKS_EFT_Setup_ACHImmDestName.valueStr())
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1162", "@SKS1028"));
            SKS_EFT_Setup_ACHImmDestName.setFocus();
            return false;
        }

        if(!SKS_EFT_Setup_ACHImmOrig.valueStr())
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1164", "@SKS1028"));
            SKS_EFT_Setup_ACHImmOrig.setFocus();
            return false;
        }

        if(!SKS_EFT_Setup_ACHImmOrigName.valueStr())
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1166", "@SKS1028"));
            SKS_EFT_Setup_ACHImmOrigName.setFocus();
            return false;
        }

        if(!SKS_EFT_Setup_ACHCompanyID.valueStr())
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SYS66152", "@SKS1028"));
            SKS_EFT_Setup_ACHCompanyID.setFocus();
            return false;
        }

        if(!SKS_EFT_Setup_ACHCompanyName.valueStr())
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SYS15581", "@SKS1028"));
            SKS_EFT_Setup_ACHCompanyName.setFocus();
            return false;
        }

        SKS_EB_FileStorage  lsks_EB_FileStorage;

        //BankFabric
        select firstonly StorageOption_APEFTFXDD from lsks_EB_FileStorage;

        // APEFT+ File Info fields
        if (lsks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFT_Setup.APBankFabricFileLocation == '')
            {
                Box::warning(strFmt("@SKS2643","@SKS:SKGBFAPFileLocation")); //required field

                APEFTSetup.clicked();

                return false;
            }
        }
        else
        {
            if(SKS_EFT_Setup.APFileName == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1237" ,"@SKS1028"));
                APEFTSetup.clicked();
                return false;
            }

            if(SKS_EFT_Setup.APFilePath == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1246" ,"@SKS1028"));
                APEFTSetup.clicked();
                return false;
            }

            if(SKS_EFT_Setup.APArchive == NoYes::Yes && SKS_EFT_Setup.APPathArchive == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS75" ,"@SKS1028"));
                APEFTSetup.clicked();
                return false;
            }
        }

        if(SKS_EFT_Setup.APCommID == '')
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", SKS_PP_Setup_CommID.label() ,"@SKS1028"));
            SKS_PP_Setup_CommID.setFocus();
            return false;
        }
    }

    if(SKS_EFT_Setup.ModAREFT == NoYes::Yes)
    {
        SKS_EB_FileStorage  lsks_EB_FileStorage;

        //BankFabric
        select firstonly StorageOption_APEFTFXDD from lsks_EB_FileStorage;

        // DD File Info fields
        if (lsks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFT_Setup.ARBankFabricFileLocation == '')
            {
                Box::warning(strFmt("@SKS2643","@SKS:SKGBFARFileLocation")); //required field

                AREFTSetup.clicked();

                return false;
            }
        }
        else
        {
            if(SKS_EFT_Setup.ARFileName == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS4141" ,"@SKS1419"));
                AREFTSetup.clicked();
                return false;
            }

            if(SKS_EFT_Setup.ARFilePath == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS4142" ,"@SKS1419"));
                AREFTSetup.clicked();
                return false;
            }

            if(SKS_EFT_Setup.ARArchive == NoYes::Yes && SKS_EFT_Setup.ARPathArchive == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS75" ,"@SKS1419"));
                AREFTSetup.clicked();
                return false;
            }
        }

        if(SKS_EFT_Setup.ARCommID == '')
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", SKS_PP_Setup_CommID.label() ,"@SKS1419"));
            SKS_PP_Setup_CommID.setFocus();
            return false;
        }

    }
    if(SKS_EFT_Setup.ModBTB == NoYes::Yes)
    {
        SKS_EB_FileStorage  lsks_EB_FileStorage;

        //BankFabric
        select firstonly StorageOption_APEFTFXDD from lsks_EB_FileStorage;

        // DD File Info fields
        if (lsks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFT_Setup.BTBBankFabricFileLocation == '')
            {
                Box::warning(strFmt("@SKS2643","@SKS:SKGBFBTBFileLocation")); //required field

                BBTSetup.clicked();

                return false;
            }
        }
        else
        {

            //AREFT+ File Info fields

            if(SKS_EFT_Setup.BTBFileName == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS:SKGBTBFileName" ,"@SKS:SKGBTBTransfer"));
                BBTSetup.clicked();
                return false;
            }

            if(SKS_EFT_Setup.BTBFilePath == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS:SKGBTBFilePath" ,"@SKS:SKGBTBTransfer"));
                BBTSetup.clicked();
                return false;
            }

            if(SKS_EFT_Setup.BTBArchive == NoYes::Yes && SKS_EFT_Setup.BTBPathArchive == '')
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS75" ,"@SKS:SKGBTBTransfer"));
                BBTSetup.clicked();
                return false;
            }
        }

        if(SKS_EFT_Setup.BTBCommID == '')
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS2430" ,"@SKS:SKGBTBTransfer"));
            return false;
        }
    }

    if(SKS_EFT_Setup.ModCNP == NoYes::Yes)
    {
        //AREFT+ File Info fields

        SKS_EB_FileStorage  lsks_EB_FileStorage;

        //BankFabric
        select firstonly StorageOption_APEFTFXDD from lsks_EB_FileStorage;

        // DD File Info fields
        if (lsks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFT_Setup.CNPBankFabricFileLocation == '')
            {
                Box::warning(strFmt("@SKS2643","@SKS:SKGBFCNPFileLocation")); //required field

                CNPSetup.clicked();

                return false;
            }
        }
        else
        {
            if(SKS_EFT_Setup.CNPFileName == '')
            {
                Box::warning("@SKS253","@SKS225"); //required field

                CNPSetup.clicked();

                return false;
            }

            if(SKS_EFT_Setup.CNPFilePath == '')
            {
                Box::warning("@SKS253","@SKS225"); //required field

                CNPSetup.clicked();

                return false;
            }

            if(SKS_EFT_Setup.CNPArchive == NoYes::Yes && SKS_EFT_Setup.CNPPathArchive == '')
            {
                Box::warning("@SKS253","@SKS225"); //required field

                CNPSetup.clicked();

                return false;
            }
        }

        if(SKS_EFT_Setup.CNPCommID == '')
        {
            Box::warning("@SKS253","@SKS225"); //required field
            return false;
        }

    }

    if(SKS_PP_Setup.ModEPAY == NoYes::Yes)
    {
        if(SKS_EPY_Setup.WebServLoc == '')
        {
            error(strFmt("@SKS3398", "@SKS3274"));
            return false;
        }
        else if(SKS_EPY_Setup.ClientId == '')
        {
            error(strFmt("@SKS3398", "@SYS69163"));
            return false;
        }
        else if(SKS_EPY_Setup.Secret == '')
        {
            error(strFmt("@SKS3398", "@SKS3378"));
            return false;
        }
        else if(SKS_EPY_Setup.JournalName == '')
        {
            error(strFmt("@SKS3398", "@SYS35283"));
            return false;
        }
        else if(SKS_EPY_Setup.PaymMode == '')
        {
            error(strFmt("@SKS3398", "@SYS21698"));
            return false;
        }
    }

    if(SKS_PP_Setup.RegMandateAutomation == NoYes::Yes
        && SKS_EFT_Setup.ModAREFT == NoYes::Yes
        && SKS_EFT_Setup.LegalId == SKS_EB_LegalID::Blank)
    {
        Error("@SKS:SKGLegalIdReqd");
        return false;
    }

    return ret;
}

public void changeFreeTextLedgerCompany(SelectableDataArea _company)
{
    RefRecId newCoaRecId;
    if (_company != currentLedgerCompany)
    {
        currentLedgerCompany = _company;
        newCoaRecId = Ledger::findByLegalEntity(CompanyInfo::findDataArea(_company).RecId).ChartOfAccounts;
        currentCOARecId = newCoaRecId;
    }
}

public void changeGLLedgerCompany(SelectableDataArea _company)
{
    RefRecId newCoaRecId;
    if (_company != currentLedgerCompany)
    {
        currentLedgerCompany = _company;
        newCoaRecId = Ledger::findByLegalEntity(CompanyInfo::findDataArea(_company).RecId).ChartOfAccounts;
        currentCOARecId = newCoaRecId;
    }
}

void disableAPEFT()
{
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup,ModAPEmail)).allowEdit(SKS_EFT_Setup.ModAPEFT);
    element.setEFTTab(SKS_EFT_Module::AP, SKS_EFT_Setup.ModAPEFT);
}

void disablePFA()
{
    EFTAP_PaymenFactoryTab.enabled(SKS_PP_Setup.ModPFA);
}

/// <summary>
/// SKS_FDD_13903
/// This method is used to enable/disable the CCR related fields
/// </summary>
void setCCRControlProperties()
{
    boolean isCCRecModActive = SKS_PP_Setup.ModCCReconciliationExtensions;
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRPath)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRInCommID)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRAutoReconcile)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRJournalName)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRAutomaticallyPostGL)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRPath)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRCurrencyAdjustmentBankTranType)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRCurrencyAdjustmentGainAccount)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRCurrencyAdjustmentLossAccount)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRGLCreationCompany)).allowEdit(false);  //SKS_VSTS_14296
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRCurrencyAdjustmentJournalName)).allowEdit(isCCRecModActive);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRBankFabricLocation)).allowEdit(SKS_PP_Setup.ModCCReconciliationExtensions && SKS_EB_FileStorage.StorageOption_CCR == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRBankFabricLocation)).visible(SKS_EB_FileStorage.StorageOption_CCR == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRPath)).allowEdit(SKS_PP_Setup.ModCCReconciliationExtensions && SKS_EB_FileStorage.StorageOption_CCR == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, CCRPath)).visible(SKS_EB_FileStorage.StorageOption_CCR == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_CCR == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);
    CCRPostedPaymentReversal.enabled(isCCRecModActive);
    CCRReverseReconciliation.enabled(isCCRecModActive);
}

void disableIHB()
{
    // FDD 9848 OD InterestCalculation -->
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_AppropriationFeeLedgerAccount)).allowEdit(SKS_PP_Setup.IHB_Active);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_OverdraftInterestLedgerAccount)).allowEdit(SKS_PP_Setup.IHB_Active);
    // FDD 9848 OD InterestCalculation <--

    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_GLJournalName)).allowEdit(SKS_PP_Setup.IHB_Active);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_InterCoMOP)).allowEdit(SKS_PP_Setup.IHB_Active);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_CalculationFrequencyStartMonth)).allowEdit(SKS_PP_Setup.IHB_Active && SKS_PP_Setup.IHB_InterestCalcFreq == SKS_IHB_InterestCalcFreq::Annual);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_CalculationFrequencyEndMonth)).allowEdit(SKS_PP_Setup.IHB_Active && SKS_PP_Setup.IHB_InterestCalcFreq == SKS_IHB_InterestCalcFreq::Annual);

    //Group Boxes
    InhouseField.enabled(SKS_PP_Setup.IHB_Active); // SKS_BUG_10387
    IHB_InterestCalc.enabled(SKS_PP_Setup.IHB_Active);
    IHB_InterestNote.enabled(SKS_PP_Setup.IHB_Active);
}

void disableAREFT()
{
    element.setEFTTab(SKS_EFT_Module::AR, SKS_EFT_Setup.ModAREFT);
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup,ModAREmail)).allowEdit(SKS_EFT_Setup.ModAREFT);
}

void disableBankToBankTransfer()
{
    element.setBBTTab(SKS_EFT_Module::BTB, SKS_EFT_Setup.ModBTB);
}

void disableCNP()
{
    element.setCNPTab(SKS_EFT_Module::CNP, SKS_EFT_Setup.ModCNP);
}

public void activateRegisteredModules(int _keyStat = -15)
{
    int locKeyStat;
    if(_keyStat == -15)
        if(element.validateUnlockKey() > 0)
            locKeyStat = true;
        else
            locKeyStat = false;
    else
        locKeyStat = _keyStat;
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,ModPosPay)).allowEdit(SKS_PP_Setup.RegPP && locKeyStat);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,ModBR)).allowEdit(SKS_PP_Setup.RegBR && locKeyStat);
    // 21186
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, ModPCard)).allowEdit(SKS_PP_Setup.RegPCard && locKeyStat);
    // 21186
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,ModLockbox)).allowEdit(SKS_PP_Setup.RegLB && locKeyStat);
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup,ModAPEFT)).allowEdit(SKS_PP_Setup.RegAPEFT && locKeyStat);
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup,ModFX)).allowEdit(SKS_PP_Setup.RegFX && SKS_EFT_Setup.ModAPEFT && locKeyStat);
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup,ModAREFT)).allowEdit(SKS_PP_Setup.RegAREFT && locKeyStat);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,ModPFA)).allowEdit(SKS_PP_Setup.RegPaymFactoryAutomation && locKeyStat);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_Active)).allowEdit(SKS_PP_Setup.RegInhouseBank && locKeyStat);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,ModMCFAPDD)).allowEdit((SKS_PP_Setup.RegAPEFT == NoYes::Yes || SKS_PP_Setup.RegAREFT == NoYes::Yes) && locKeyStat);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, ModCCReconciliationExtensions)).allowedit(SKS_PP_Setup.RegCCReconciliationExtensions && locKeyStat);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, ModSCF)).allowEdit(SKS_PP_Setup.RegSCF && locKeyStat); // FDD-14323- Supply chain finance
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, ModBTB)).allowEdit(SKS_PP_Setup.RegBTB && locKeyStat);
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, ModCNP)).allowEdit(SKS_PP_Setup.RegCNP && locKeyStat);
}

public void enableDisableRegFields()
{
    if(strFind(SKS_PP_Setup.RegUnlockKey, ':', 0, strLen(SKS_PP_Setup.RegUnlockKey)))
    {
        //used for new key format since values are populated automatically
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegPP)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegBR)).allowEdit(false);
        // 21186
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, RegPCard)).allowEdit(false);
        // 21186
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegLB)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegAPEFT)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegAREFT)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegFX)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegPaymFactoryAutomation)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegInhouseBank)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegMandateAutomation)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,regMCFAPDD)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,regCCReconciliationExtensions)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,EFTLicensedFormats)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegSCF)).allowEdit(false); // FDD-14323- Supply chain finance
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegBTB)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegCNP)).allowEdit(false);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegPP)).allowEdit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegBR)).allowEdit(true);
        // 21186
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, RegPCard)).allowEdit(true);
        // 21186
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegLB)).allowEdit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegAPEFT)).allowEdit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegFX)).allowEdit(SKS_PP_Setup.RegAPEFT);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegAREFT)).allowEdit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,EFTLicensedFormats)).allowEdit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegInhouseBank)).allowEdit(SKS_PP_Setup.RegBR && SKS_PP_Setup.RegPaymFactoryAutomation && SKS_PP_Setup.RegAPEFT);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegPaymFactoryAutomation)).allowEdit(SKS_PP_Setup.RegAPEFT);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegMandateAutomation)).allowEdit(SKS_PP_Setup.RegAREFT);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,regMCFAPDD)).allowEdit(SKS_PP_Setup.RegAPEFT == NoYes::Yes || SKS_PP_Setup.RegAREFT == NoYes::Yes);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,regCCReconciliationExtensions)).allowEdit(SKS_PP_Setup.RegBR);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegSCF)).allowEdit(true); // FDD-14323- Supply chain finance
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegBTB)).allowEdit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegCNP)).allowEdit(true);

        if(!SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegInhouseBank)).allowEdit())
        {
            SKS_PP_Setup.RegInhouseBank = NoYes::No;
        }
        if(!SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegPaymFactoryAutomation)).allowEdit())
        {
            SKS_PP_Setup.RegPaymFactoryAutomation = NoYes::No;
        }
        if(!SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegMandateAutomation)).allowEdit())
        {
            SKS_PP_Setup.RegMandateAutomation = NoYes::No;
        }
        if(!SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,regMCFAPDD)).allowEdit())
        {
            SKS_PP_Setup.RegMCFAPDD = NoYes::No;
        }
        if(!SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,regCCReconciliationExtensions)).allowEdit())
        {
            SKS_PP_Setup.regCCReconciliationExtensions = NoYes::No;
        }

        if(!SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegAPEFT)).allowEdit())
        {
            SKS_PP_Setup.RegFX = NoYes::No;
        }
        //FDD-14323- Supply chain finance
        if(!SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,RegSCF)).allowEdit())
        {
            SKS_PP_Setup.RegSCF = NoYes::No;
        }
    }

}

// 
void setLBControlProperties()
{        
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,LBPath)).visible(SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);     
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,LBBankFabricLocation)).visible(SKS_EB_FileStorage.StorageOption_LB== SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);        

    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,LBExternalPath)).visible(SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,LBBankFabricExtLocation)).visible(SKS_EB_FileStorage.StorageOption_LB== SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);

    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,LBPathArchive)).visible(SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);
    //SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,LBArchive)).visible(SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue); //Issue21251

    FileNameTab.visible(SKS_EB_FileStorage.StorageOption_PP == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);   // 17274
}

void setBRControlProperties()
{
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoReconcile)).allowEdit(SKS_PP_Setup.ModBR && SKS_PP_Setup.BRCreateBankStatements);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRCreateBankStatementsIfNoTrans)).allowEdit(SKS_PP_Setup.ModBR && SKS_PP_Setup.BRCreateBankStatements);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRNotifyStatements)).allowEdit(SKS_PP_Setup.ModBR && SKS_PP_Setup.BRCreateBankStatements && SKS_PP_Setup.BRCreateBankStatementsIfNoTrans);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRNotifyMinusDays)).allowEdit(SKS_PP_Setup.ModBR && SKS_PP_Setup.BRNotifyStatements && SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRNotifyStatements)).allowEdit());
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BREmail)).allowEdit(SKS_PP_Setup.ModBR && SKS_PP_Setup.BRNotifyStatements && SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRNotifyStatements)).allowEdit()); //SKS_20603
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRBatchJobId)).allowEdit(SKS_PP_Setup.ModBR && SKS_PP_Setup.BRNotifyStatements && SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRNotifyStatements)).allowEdit());
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRBankHolidayGroupID)).allowEdit(SKS_PP_Setup.ModBR && SKS_PP_Setup.BRNotifyStatements && SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRNotifyStatements)).allowEdit());
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRPath)).allowEdit(SKS_PP_Setup.ModBR && SKS_EB_FileStorage.StorageOption_BR == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRPath)).visible(SKS_EB_FileStorage.StorageOption_BR == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_BR == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRBankFabricLocation)).allowEdit(SKS_PP_Setup.ModBR && SKS_EB_FileStorage.StorageOption_BR == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRBankFabricLocation)).visible(SKS_EB_FileStorage.StorageOption_BR == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue);
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BREnableOldClickNMatchRules)).allowEdit(SKS_PP_Setup.ModBR);

    if(SKS_PP_Setup.ModBR == NoYes::Yes)
    {
        element.setBRImportedAndCustSettlementProperties();
        element.setManualEntryProperties();
        element.setManualEntrySettings();
    }

    // SKS_BR5 <--
}

