void disableBankRec()
{
    // ///////////////    NEVER ADD CODE TO THIS METHOD  \\\\\\\\\\\\\\\\\\\\\\\\\
    // Never add code to this method.  Add any new controls to method setBRControlProperties().
    // Follow the examples there.  Your contol's enable/disable should be set in ONE LINE for the entire FORM.
    // We should NOT have multiple places that set the control's enable property directly
    // the controls here are in the process of being moved to the setBRControlProperties() method.
    if(SKS_PP_Setup.ModBR == NoYes::Yes)
    {
        // ///////////////    NEVER ADD CODE TO THIS METHOD  \\\\\\\\\\\\\\\\\\\\\\\\\
        // See note at top of method
        SKS_PP_Setup_BRJournalName.enabled(true);
        SKS_PP_SETUP_BRCreateBankStatements.enabled(true);
        SKS_PP_SETUP_BRCreateBankStatements.modified();
        SKS_PP_SETUP_BRAutomaticallyPostGL.enabled(true);
        SKS_PP_SETUP_BRInCommID.enabled(true);
        SKS_PP_Setup_BRGLCreationCompany.enabled(true);

    }
    else
    {
        // ///////////////    NEVER ADD CODE TO THIS METHOD  \\\\\\\\\\\\\\\\\\\\\\\\\
        // See note at top of method
        SKS_PP_Setup_BRJournalName.enabled(false);
        SKS_PP_SETUP_BRCreateBankStatements.enabled(false);
        SKS_PP_SETUP_BRAutomaticallyPostGL.enabled(false);
        SKS_PP_SETUP_BRInCommID.enabled(false);
        SKS_PP_Setup_BRGLCreationCompany.enabled(False);
    }
    element.setBRControlProperties();  //All new controls go in this method.
    // ///////////////    NEVER ADD CODE TO THIS METHOD  \\\\\\\\\\\\\\\\\\\\\\\\\
    // See note at top of method
}

void disableLockbox()
{
    if(SKS_PP_Setup.ModLockbox == NoYes::No)
    {
        element.setLockboxTab(false);
    }
    else
    {
        element.setLockboxTab(true);

    }
}

//FDD-14323- Supply chain finance
