{
    if(SKS_PP_Setup.ModSCF == NoYes::No)
    {
        element.setSCFTab(false);
    }
    else
    {
        element.setSCFTab(true);

    }
}

void enabledisablePayJournlfields()
{
    if(SKS_PP_Setup.SCFAutoPmtInvJrnl == NoYes::Yes)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, SCFAutoPmtInvPost)).allowEdit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, SCFCreateNoMismatch)).allowEdit(true);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, SCFAutoPmtInvPost)).allowEdit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, SCFCreateNoMismatch)).allowEdit(false);

        SKS_PP_Setup.SCFAutoPmtInvPost = NoYes::No;
        SKS_PP_Setup.SCFCreateNoMismatch = NoYes::No;
    }
}

//FDD-11448
void disableRepayNonCompound()
{
    if(SKS_PP_Setup.IHB_Active && SKS_PP_Setup.IHB_InterestCalcMethod == SKS_IHB_InterestCalcMeth::Noncompounded)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_InterestRepaymentNonCompounded)).enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_InterestRepaymentNonCompounded)).mandatory(true);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_InterestRepaymentNonCompounded)).enabled(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,IHB_InterestRepaymentNonCompounded)).mandatory(false);
        SKS_PP_Setup.IHB_InterestRepaymentNonCompounded = '';
    }
}

public void ePayNotActivated()
{
    if(SKS_EPY_Setup.Activated == SKS_EPY_Activated::Activated)
    {
        SKS_EPY_Setup.Activated = SKS_EPY_Activated::NotActivated;
        element.DeleteEpayCurrencyTable();
        warning("@SKS3438");
    }
}

public void init()
{
    SKS_PP_Sys tmpSKS_PP_Sys = new SKS_PP_Sys();
    boolean setupValid;

    // Need to initiliaze number sequences before numberSeqPreInit is called
    // Number sequences sometimes get initialized in validatesetup (upgrade code)
    setupValid = tmpSKS_PP_Sys.ValidateSetup(this.name());

    activationCompleted = false;

    super();

    SKS_PP_Setup_SCFOffsetLedgerDimension.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(SKS_PP_Setup_DS, fieldStr(SKS_PP_Setup, SCFOffsetLedgerDimension), fieldStr(SKS_PP_Setup, SCFAccountType)));
    SKS_PP_Setup_SCFOffsetLedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);
    SKS_PP_Setup_SCFOffsetLedgerDimension.parmValidateBlockedForManualEntry(true);

    this.numberSeqPreInit();

    if ( !setupValid)
    {
        this.close();
    }
    else
    {

        select * from SKS_PP_Setup;
        select * from SKS_EFT_Setup;
        select firstonly SKS_EB_FileStorage;
        element.activateRegisteredModules();
        if(SKS_PP_Setup.SetUpID == '')//if no initial record, create one...
        {
            if(!SKS_EB_FileStorage)
            {
                Box::warning(strFmt("@SKS:SKGNewTASCustomer", SKS_PP_Sys::getSKSVersion()), "@SKS311");
            }

            SKS_PP_Setup.SetUpID = 'DD';
            SKS_PP_Setup.BREdition = SKS_BR_Edition::Advanced;
            SKS_PP_Setup.PPNextFileNbr = '000000';
            SKS_PP_Setup.PPFilePeriodsRet = 24;
            SKS_PP_Setup.LBFilePeriodsRet = 24;
            SKS_PP_Setup.LBRedSettleInvBal = 1;
            SKS_PP_Setup.LBRedSettleCRBal  = 1;
            SKS_PP_Setup.BRGLCreationCompany = SKS_PP_Setup.company();
            SKS_PP_Setup.CCRGLCreationCompany = SKS_PP_Setup.company(); //SKS_VSTS_14296
            SKS_PP_Setup.CCCustCrdrCrdARSync = NoYes::Yes;
            SKS_PP_Setup.CCAuthCodeValue = SKS_CC_AuthCodeValue::OriginationId;
            SKS_PP_Setup.CCUseOneTimeCustomerLogic = NoYes::Yes;
            SKS_PP_Setup.LBAutoServerSide = NoYes::Yes;
            SKS_PP_Setup.PPOutServerSideFileProc = NoYes::Yes;
            SKS_PP_Setup.doInsert();
            SysOperationSandbox::callStaticMethod(classNum(SKS_PP_Upgrade),
                staticMethodStr(SKS_PP_Upgrade, runUpgrade),
                conNull(),
                "@SKS:SKGUpgradingTAS",
                "@SKS978");
            SKS_PP_Setup_ds.reread();
        }

        if(SKS_EFT_Setup.SetUpID == '')//if no initial record, create one...
        {
            SKS_EFT_Setup.SetUpID = 'DD';
            SKS_EFT_Setup.APPeriodsRet = 24;
            SKS_EFT_Setup.APInclDR = 1;
            SKS_EFT_Setup.ARPeriodsRet = 24;
            SKS_EFT_Setup.ARInclCR = 1;
            SKS_EFT_Setup.APWhenMarkSent = SKS_EFT_WhenMarkSent::Completion;
            SKS_EFT_Setup.ARWhenMarkSent = SKS_EFT_WhenMarkSent::Completion;
            SKS_EFT_Setup.BTBWhenMarkSent = SKS_EFT_WhenMarkSent::Completion;
            SKS_EFT_Setup.APACHRecordSummary = SKS_EFT_VendorRecordSummary::Payment;
            SKS_EFT_Setup.ARACHRecordSummary = SKS_EFT_VendorRecordSummary::Payment;
            SKS_EFT_Setup.APCreateVendeBankrecord = NoYes::No;
            SKS_EFT_Setup.ARCreateCustBankrecord = NoYes::No;
            SKS_EFT_Setup.APServerSideFiles = NoYes::Yes;
            SKS_EFT_Setup.ARServerSideFiles = NoYes::Yes;
            SKS_EFT_Setup.APFormatValidJournal = NoYes::Yes;
            SKS_EFT_Setup.DDFormatValidJournal = NoYes::Yes;
            SKS_EFT_Setup.BTBServerSideFiles = NoYes::Yes;

            // Setup options
            // Bank account info tab
            SKS_EFT_Setup.ACHImmDest = 'ImmedDestID';
            SKS_EFT_Setup.ACHImmDestName = 'ImmedDestName';
            SKS_EFT_Setup.ACHImmOrig = 'ImmedOrigID';
            SKS_EFT_Setup.ACHImmOrigName = 'ImmedOrigName';
            SKS_EFT_Setup.ACHCompanyID = 'CompanyID';
            SKS_EFT_Setup.ACHCompanyName = 'CompanyName';
            SKS_EFT_Setup.ACHOrigDFI = 'OrigDFIID';
            SKS_EFT_Setup.ACHOrigDFIName = 'OrigDFIName';

            SKS_EFT_Setup.APCreateSendLeter = NoYes::Yes;
            SKS_EFT_Setup.APUpdatePaymPostingDate = NoYes::No;
            SKS_EFT_Setup.APPrintAuditChg = NoYes::Yes;

            // AP EFT+ setup
            // General tab
            SKS_EFT_Setup.APCreateVendeBankrecord = NoYes::Yes;
            SKS_EFT_Setup.APWhenMarkSent = SKS_EFT_WhenMarkSent::FileCreation;

            // APEFT+ file info tab
            SKS_EFT_Setup.APFileName = 'apeft.xml';
            SKS_EFT_Setup.APFileAddEBNbr = NoYes::Yes;
            SKS_EFT_Setup.APFileNameConnector = '_';

            SKS_EFT_Setup.doInsert();

        }

        if(!SKS_EB_FileStorage)
        {
            SKS_EB_FileStorage.insert();
        }
        select SKS_EPY_Setup;
        if(!SKS_EPY_Setup)
        {
            SKS_EPY_Setup.FTInv = NoYes::Yes;
            SKS_EPY_Setup.SOInv = NoYes::Yes;
            SKS_EPY_Setup.doInsert();
        }  

        element.regInfo();
        element.setPositivePayTab();
        element.disableBankRec();
        element.disableLockbox();
        //element.disableAPEFT();   //18845
        //element.disableAREFT();   //18845
        element.disableCreditCard();
        element.setCCRControlProperties();
        element.setEPayTab();
        element.disableIHB();
        element.disablePFA();
        //element.setPayLinkTab();
        element.setInhouseTab();// FDD9652 Dayend process
        element.disableODEmailFields(); // FDD 9848 OD InterestCalculation
        element.disableRepayNonCompound(); //FDD-11448
        element.disableMCFAPDD();
        element.disableSCF(); // FDD-14323- Supply chain finance
        element.disableBankToBankTransfer(); //FDD-14181- Bank to bank transfer
        element.disableCNP(); //FDD- Customer reimbursement

        this.numberSeqPostInit();

        gChangedLBPath = false;

        EmailBody.text("@SKS:SKGEmailBodyText");
        ContactText.text("@SKS2");
        EmailContact.text("@SKS5");
        if(SKS_PP_Setup.RegAuthorizedUsers < 300)
        {
            ImportantLicenseTabPage.visible(true);
            LicensingNote.visible(true);
            LicensingNote.text("@SKS2614" + ' ' + "@SKS2615" + ' ' + "@SKS2616" + ' ' + "@SKS2617" + ' ' + "@SKS2618" + ' ' + "@SKS2619");
        }
        else
        {
            ImportantLicenseTabPage.visible(false);
            LicensingNote.visible(false);
        }
    }
}

void numberSeqPostInit()
{
    allowSameAs.visible(numberSeqApplicationModule.sameAsActive());
    labelSameAs.visible(allowSameAs.visible());
}

void numberSeqPreInit()
{
    runExecuteDirect   = false;

    numberSequenceModules = [NumberSeqModule::Lockbox, NumberSeqModule::APEFTPlus, NumberSeqModule::AREFTPlus, NumberSeqModule::APEFTPlusInbound, NumberSeqModule::EPay, NumberSeqModule::PosPay, NumberSeqModule::DDMandateDataDelivery, NumberSeqModule::SCF, NumberSeqModule::BTB, NumberSeqModule::CNP , NumberSeqModule::IHB]; // SKS_Bug_10978  //FDD-14323
    numberSeqApplicationModule = new NumberSeqModuleLockbox();
    scope = NumberSeqScopeFactory::createDataAreaScope();
    NumberSeqApplicationModule::createReferencesMulti(numberSequenceModules, scope);
    tmpIdRef.setTmpData(NumberSequenceReference::configurationKeyTableMulti(numberSequenceModules));
}

void RefreshParentInfo()
{
    SKS_EFT_Setup_ds.reread();
}

void regInfo()
{
    //SysLicenseExpireDateStr expireDate = xSysConfig::find(ConfigType::LicenseName,1).Value;

    CustomerName.text(xInfo::licenseName());
    SKS_PP_Setup.RegAuthorizedUsers = SKS_EB_SysInfo::RegisteredUsers();
    SKS_PP_Setup.RegCustomerID = element.getTASSerialNumber();
    SKS_PP_Setup.RegVersion = SKS_PP_Sys::getSKSVersion();
    SKS_PP_Setup.doUpdate();
}

private str getTASSerialNumber()
{
    SKS_PP_Setup locSKS_PP_Setup;
    str rtnval = SKS_PP_StringFormats::strRight(xInfo::serialNo(),12);
    select firstonly crosscompany RecId, RegCustomerID from locSKS_PP_Setup where locSKS_PP_Setup.RegCustomerID != '';
    if(locSKS_PP_Setup)
    {
       if(strLen(locSKS_PP_Setup.RegCustomerID) < 12)
       {
           rtnval = SKS_PP_StringFormats::strRight(xInfo::serialNo(),6);
       }
    }
    return rtnval; 

}

public void SendToePay()
{
    CustTable locCustTable;
    CustTrans locCustTrans;
    CustTransOpen locCustTransOpen;

    While
        select recid
        from locCustTable
        where locCustTable.SKS_EPY_CustFilter == SKS_EPY_CustFilter::Yes
            || locCustTable.SKS_EPY_CustFilter == SKS_EPY_CustFilter::YesByFilter
        join forupdate locCustTransOpen
        where locCustTransOpen.AccountNum == locCustTable.AccountNum
            && locCustTransOpen.SKS_EPY_SentToePay == NoYes::No
        join TransType from locCustTrans
        where locCustTrans.AccountNum == locCustTransOpen.AccountNum
            && locCustTrans.RecId == locCustTransOpen.RefRecId
            && ((locCustTrans.TransType == LedgerTransType::Sales && SKS_EPY_Setup.SOInv)
                || (locCustTrans.TransType == LedgerTransType::Cust && SKS_EPY_Setup.FTInv)
                || (locCustTrans.TransType == LedgerTransType::Project && SKS_EPY_Setup.ProjInv))
    {
        ttsBegin;
        locCustTransOpen.Update();
        ttsCommit;
    }
}

