void setEFTTab(SKS_EFT_Module _eftModule, boolean _value)
{
    boolean visibleValue;
    ;

    if (SKS_EFT_Setup.ModAPEFT == NoYes::Yes || SKS_EFT_Setup.ModAREFT == NoYes::Yes || SKS_EFT_Setup.ModBTB == NoYes::Yes || SKS_EFT_Setup.ModCNP == NoYes::Yes)
    {
        visibleValue = true;
    }
    else
    {
        visibleValue = false;
    }

    DefaultFromBank.enabled(visibleValue);
    SKS_EFT_Setup_ACHImmDest.enabled(visibleValue);
    SKS_EFT_Setup_ACHImmDestName.enabled(visibleValue);
    SKS_EFT_Setup_ACHImmOrig.enabled(visibleValue);
    SKS_EFT_Setup_ACHIMMOrigName.enabled(visibleValue);
    SKS_EFT_Setup_ACHCompanyID.enabled(visibleValue);
    SKS_EFT_Setup_ACHCompanyName.enabled(visibleValue);
    SKS_EFT_Setup_ACHOrigDFI.enabled(visibleValue);
    SKS_EFT_Setup_ACHOrigDFIName.enabled(visibleValue);
    SKS_EFT_Setup_ACHFileIDModifier.enabled(visibleValue);
    SKS_EFT_Setup_FileCrLf.enabled(visibleValue);
    SKS_EFT_Setup_FillBlock.enabled(visibleValue);

    if (_eftModule == SKS_EFT_Module::AP)
    {
        APEFTSetup.enabled(_value);
    }

    if (_eftModule == SKS_EFT_Module::AR)
    {
        AREFTSetup.enabled(_value);
    }

    if (_eftModule == SKS_EFT_Module::BTB)
    {
        AREFTSetup.enabled(_value);
    }
}

void setEPayTab()
{
    boolean visiblevalue;
    ;

    if(SKS_PP_Setup.ModEPAY == NoYes::Yes)
    {
        visiblevalue = true;
    }
    Else
    {
        visiblevalue = false;
    }

    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, batchjobid)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, EPYAutoPost)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, FTInv)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, SOInv)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, PaymMode)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, ClientId)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, Secret)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, WebServLoc)).allowEdit(visiblevalue);
    sks_epy_setup_ds.object(fieldNum(SKS_EPY_Setup, PaymentCreation)).allowEdit(visiblevalue);
