/// <summary>
/// FDD9652 enable/disable of inhouse fields
/// </summary>
void setInhouseTab()
{
    if (InhouseField_IHB_SendEmail.value() == NoYes::Yes)
    {
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_FileType)).enabled(true);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_AttachTemplate)).enabled(true);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_TemplateLanguage)).enabled(true);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_ReplyToEmail)).enabled(true);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_ReplyToName)).enabled(true);
    }
    else
    {
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_FileType)).enabled(false);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_AttachTemplate)).enabled(false);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_TemplateLanguage)).enabled(false);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_ReplyToEmail)).enabled(false);
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, IHB_ReplyToName)).enabled(false);
    }
    if(InhouseField_IHB_CreateFile.value() == NoYes::Yes)
    {
        //BankFabric
        select firstonly sks_EB_FileStorage;
        if (sks_EB_FileStorage.StorageOption_IHB == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            sks_pp_setup_ds.object(fieldNum(SKS_Pp_Setup, IHBBankFabricLocation)).enabled(true);
        }
        else
        {
            sks_pp_setup_ds.object(fieldNum(SKS_Pp_Setup, IHB_FilePath)).enabled(true);
            sks_pp_setup_ds.object(fieldNum(SKS_Pp_Setup, IHBBankFabricLocation)).enabled(false);
        }
    }
    else
    {
        sks_pp_setup_ds.object(fieldNum(SKS_Pp_Setup, IHB_FilePath)).enabled(false);
    }

}

