/// <summary>
/// CCA-Used to update PayLink queue records for new enum value
/// </summary>
public void updatePayLinkQueue()
{
    SKS_CC_PayLinkQueue payLinkQueue;

    select firstonly1 RecId from payLinkQueue;
    if (payLinkQueue.RecId)
    {
        SKS_CC_PayLinkProcessStatusEnumUpdForConv::updatePayLinkQueue();
    }

    SKS_PP_Setup.CCPayLinkEnumUpgraded = NoYes::Yes;
}

/// <summary>
/// Method used to verify the supplied module is registered
/// </summary>
public boolean ValidateRegistered(NoYes _Module, NoYes _Reg, boolean _ret)
{
    boolean rtnval = _ret;

    if(_Module && (!rtnval || !_Reg || !element.validateUnlockKey()))
    {
        error("@SKS:SKGModuleNotRegistered");
        rtnval = false;
    }
    return rtnval;
}

