/// <summary>
///  Method sets the ePay Currency value.
/// </summary>
private void PopulateePayCurrency()
{
    Currency            Currency;
    CurrencyCode        currencycode;
    SKS_EPY_Currency    SKS_EPY_Currency;
    sks_epy_Currency    OldCurrency;
    ;
    ttsbegin;
    currencycode = CompanyInfoHelper::standardCurrency();
    select firstonly forupdate SKS_EPY_Currency where sks_EPY_Currency.currencyCode == currencycode;
    if(!SKS_EPY_Currency)
    {
        select firstonly forupdate OldCurrency where OldCurrency.FunctionalCurrency == true;
        if(OldCurrency)
        {
            OldCurrency.FunctionalCurrency = false;
            OldCurrency.update();
        }
        select symbol, Txt from Currency where Currency.CurrencyCode == currencycode;
        SKS_EPY_Currency.currencyCode = currencycode;
        SKS_EPY_Currency.symbol = Currency.symbol;
        SKS_EPY_Currency.txt = Currency.txt;
        SKS_EPY_Currency.functionalCurrency = true;
        SKS_EPY_Currency.insert();
    }
    else
    {
        select firstonly forupdate OldCurrency where OldCurrency.FunctionalCurrency == true;
        if(oldCurrency.recid != SKS_EPY_Currency.recid)
        {
            OldCurrency.FunctionalCurrency = false;
            OldCurrency.update();
        }
        SKS_EPY_Currency.FunctionalCurrency = true;
        sks_EPY_Currency.update();
    }
    ttscommit;
}

//SKS_Bug_12085 -->
protected boolean checkIHBFieldsCanChange(SKS_PP_Setup _sks_pp_setup)
{
    boolean             ret = true;
    container           ihbBankContainer;
    int                 containerLength;
    SKS_PP_Setup        setup = _sks_pp_setup;
    SKS_PP_Setup        sks_pp_setupOrig = setup.orig();
    str                 bankStr;

    if((sks_pp_setupOrig.IHB_InterestCalcFreq != setup.IHB_InterestCalcFreq) || (sks_pp_setupOrig.IHB_AppropriationfeeID != setup.IHB_AppropriationfeeID) ||(sks_pp_setupOrig.IHB_CalculationFrequencyEndMonth != setup.IHB_CalculationFrequencyEndMonth)
      ||(sks_pp_setupOrig.IHB_CalculationFrequencyStartMonth != setup.IHB_CalculationFrequencyStartMonth)
      ||(sks_pp_setupOrig.IHB_InterestCalcMethod != setup.IHB_InterestCalcMethod) ||(sks_pp_setupOrig.IHB_InterestMarkupId != setup.IHB_InterestMarkupId)
      ||(sks_pp_setupOrig.IHB_InterestDaysCalcMethod != setup.IHB_InterestDaysCalcMethod)
      ||(sks_pp_setupOrig.IHB_OverdraftInterestID != setup.IHB_OverdraftInterestID))
    {
        ihbBankContainer = element.getIHBInterestCalculatedBank();
        containerLength = conLen(ihbBankContainer);
        if(containerLength >=1)
        {
            // add space between each bank
            for(int i = 1; i <= containerLength; i++)
            {
                bankStr += conPeek(ihbBankContainer, i);
                if(containerLength != i)
                    bankStr += ', ';
            }
            throw error(strfmt("@SKS:SKGIHBValuesCantbeChanged", bankStr));
        }
    }

    return ret;
}

protected container getIHBInterestCalculatedBank()
{
    container         ihbBankContainer;
    SKS_PP_Bank       sks_pp_bank;
    SKS_IHB_CalculatedInterestSummary   sks_IHB_CalculatedInterestSummary;

    while select BankAccountID from sks_pp_bank
        where sks_pp_bank.SKS_IHBInhouseBank == NoYes::Yes &&
        sks_pp_bank.IHB_UseInterestCalcDefaults == NoYes::Yes
    {
        select firstonly BankAccountID from sks_IHB_CalculatedInterestSummary
            where sks_IHB_CalculatedInterestSummary.BankAccountID == sks_pp_bank.BankAccountId;
        if(sks_IHB_CalculatedInterestSummary.BankAccountID)
        {
            ihbBankContainer += sks_pp_bank.BankAccountId;
        }
    }
    return ihbBankContainer;

}

/// <summary>
///  Method to update the ePay customer's email purpose
/// </summary>
public void UpdateePayCustomerEmailPurpose()
{
    CustTable lCustTable;
    while select lCustTable where lCustTable.SKS_EPY_CustFilter == SKS_EPY_CustFilter::Yes || lCustTable.SKS_EPY_CustFilter == SKS_EPY_CustFilter::YesByFilter
    {
        SKS_Table_CustTable::QueueCustomer(lCustTable, SKS_EPY_ChangeType::Updated,
            SKS_EPY_Table::CustTable, lCustTable.RecId);
    }
}

