/// <summary>
/// imported and cust settlement properties
/// </summary>
public void setBRImportedAndCustSettlementProperties()
{
    LedgerJournalName ledgerimportJournalName = LedgerJournalName::find(SKS_PP_Setup.BRJournalName);
    if((ledgerimportJournalName.ApproveActive == NoYes::Yes || ledgerimportJournalName.WorkflowApproval == NoYes::Yes) && SKS_PP_Setup.BRJournalName)
    {
        // for imported journal entry
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoSubmitWorkFlow)).enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoSubmitWorkFlow)).allowEdit(true);

        SKS_PP_Setup.BRAutomaticallyPostGL = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutomaticallyPostGL)).enabled(false);

        // for customer settlement
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoSubmitCustSettlementWorkFlow)).enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoSubmitCustSettlementWorkFlow)).allowEdit(true);

        SKS_PP_Setup.BRAutoPostCustSettlement = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoPostCustSettlement)).enabled(false);
    }
    else
    {
        // for imported journal entry
        SKS_PP_Setup.BRAutoSubmitWorkFlow = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoSubmitWorkFlow)).enabled(false);

        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutomaticallyPostGL)).enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutomaticallyPostGL)).allowEdit(true);

        // for customer settlement
        SKS_PP_Setup.BRAutoSubmitCustSettlementWorkFlow = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoSubmitCustSettlementWorkFlow)).enabled(false);

        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoPostCustSettlement)).enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoPostCustSettlement)).allowEdit(true);
    }
}

/// <summary>
///  Manual entry properties
/// </summary>
public void setManualEntryProperties()
{
    LedgerJournalName manualJournalName = LedgerJournalName::find(SKS_PP_Setup.BRManualJournalName);
    if((manualJournalName.ApproveActive == NoYes::Yes || manualJournalName.WorkflowApproval == NoYes::Yes) && SKS_PP_Setup.BRManualJournalName)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRManualAutoSubmitWorkFlow)).enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRManualAutoSubmitWorkFlow)).allowEdit(true);

        SKS_PP_Setup.BRAutoPostManualEntries = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoPostManualEntries)).enabled(false);
    }
    else
    {
        SKS_PP_Setup.BRManualAutoSubmitWorkFlow = NoYes::No;
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRManualAutoSubmitWorkFlow)).enabled(false);

        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoPostManualEntries)).enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRAutoPostManualEntries)).allowEdit(true);
    }
}

/// <summary>
///  Manual entry settings
/// </summary>
public void setManualEntrySettings()
{
    if(SKS_PP_Setup.BRManualEntry)
    {
        SKS_PP_Setup_BRDefaultFromImportJournal.enabled(true);
        SKS_PP_Setup_BRAutoPostManualEntries.enabled(true);
        SKS_PP_Setup_BRManualJournalName.enabled(true);
        SKS_PP_Setup_BRManualAutoSubmitWorkFlow.enabled(true);

        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BRManualJournalName)).mandatory(true);
    }
    else
    {
        SKS_PP_Setup.BRManualJournalName = SKS_PP_Setup.BRJournalName;
        SKS_PP_Setup.BRDefaultFromImportJournal = NoYes::No;
        SKS_PP_Setup.BRAutoPostManualEntries = NoYes::No;
        SKS_PP_Setup.BRManualAutoSubmitWorkFlow = NoYes::No;

        SKS_PP_Setup_BRDefaultFromImportJournal.enabled(false);
        SKS_PP_Setup_BRAutoPostManualEntries.enabled(false);
        SKS_PP_Setup_BRManualJournalName.enabled(false);
        SKS_PP_Setup_BRManualAutoSubmitWorkFlow.enabled(false);
    }

    if(SKS_PP_Setup.BRDefaultFromImportJournal)
    {
        SKS_PP_Setup.BRManualJournalName = SKS_PP_Setup.BRJournalName;
        SKS_PP_Setup.BRAutoPostManualEntries = SKS_PP_Setup.BRAutomaticallyPostGL;
        SKS_PP_Setup.BRManualAutoSubmitWorkFlow = SKS_PP_Setup.BRAutoSubmitWorkFlow;

        SKS_PP_Setup_BRAutoPostManualEntries.enabled(false);
        SKS_PP_Setup_BRManualJournalName.enabled(false);
        SKS_PP_Setup_BRManualAutoSubmitWorkFlow.enabled(false);
    }
}

/// <summary>
/// Default from import journal settings
/// </summary>
public void setDefaultFromImportJournal()
{
    if(SKS_PP_Setup.BRDefaultFromImportJournal)
    {
        SKS_PP_Setup.BRManualJournalName = SKS_PP_Setup.BRJournalName;
        SKS_PP_Setup.BRAutoPostManualEntries = SKS_PP_Setup.BRAutomaticallyPostGL;
        SKS_PP_Setup.BRManualAutoSubmitWorkFlow = SKS_PP_Setup.BRAutoSubmitWorkFlow;

        SKS_PP_Setup_BRAutoPostManualEntries.enabled(false);
        SKS_PP_Setup_BRManualJournalName.enabled(false);
        SKS_PP_Setup_BRManualAutoSubmitWorkFlow.enabled(false);
    }
    else
    {
        SKS_PP_Setup.BRManualJournalName = SKS_PP_Setup.BRJournalName;
        SKS_PP_Setup.BRAutoPostManualEntries = SKS_PP_Setup.BRAutomaticallyPostGL;
        SKS_PP_Setup.BRManualAutoSubmitWorkFlow = SKS_PP_Setup.BRAutoSubmitWorkFlow;

        SKS_PP_Setup_BRAutoPostManualEntries.enabled(true);
        SKS_PP_Setup_BRManualJournalName.enabled(true);
        SKS_PP_Setup_BRManualAutoSubmitWorkFlow.enabled(true);
    }
}

public int active()
{
    int ret;

    if(SKS_PP_Setup.LBAutoDefaultCust == '')
    {
        SKS_PP_Setup.LBAutoIgnoreErr = NoYes::No;
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, LBAutoIgnoreErr)).allowEdit(false);
    }
    else
    {
        sks_pp_setup_ds.object(fieldNum(SKS_PP_Setup, LBAutoIgnoreErr)).allowEdit(true);
    }

    if(!SKS_PP_Setup.LBAutoPmtJrnl)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, moveGoodPaymToBatch)).allowedit(false);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, moveGoodPaymToBatch)).allowedit(true);
    }

    if(SKS_PP_Setup.MoveGoodPaymToBatch == NoYes::Yes)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, nopartialsettle)).allowedit(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, paymmustbezero)).allowedit(true);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, nopartialsettle)).allowedit(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, paymmustbezero)).allowedit(false);
    }

    if(SKS_PP_Setup.LBRedSettleCRBal)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, LBWriteOffRuleCROverSettle)).allowedit(true);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, LBWriteOffRuleCROverSettle)).allowedit(false);
    }

    if(SKS_PP_Setup.FFTPM == NoYes::Yes)
    {
        TPMDedCatSetup.enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, TPMRemainShortpaid)).allowedit(true);
    }
    else
    {
        TPMDedCatSetup.enabled(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, TPMRemainShortpaid)).allowedit(false);
        SKS_PP_Setup.TPMRemainShortpaid = false;
    }

    if(SKS_PP_Setup.LBAutoPmtJrnl || SKS_PP_Setup.LBPmtPost)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, LBAutoCreateDepSlip)).allowEdit(true);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, LBAutoCreateDepSlip)).allowEdit(false);
    }

    if(SKS_PP_Setup.LBStandardDed)
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, LBDeductionType)).allowedit(true);
    }
    else
    {
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, LBDeductionType)).allowedit(false);
    }

    if(SKS_PP_Setup.CCRGLAccountRestriction == NoYes::Yes)
    {
        SKS_PP_Setup_CCRGLMaintaince.enabled(true);
    }
    else
    {
        SKS_PP_Setup_CCRGLMaintaince.enabled(false);
    }

    element.changeFreeTextLedgerCompany(SKS_LB_Setup_AutoCRParms.CustomerCompany ? SKS_LB_Setup_AutoCRParms.CustomerCompany : curext());
    element.changeGLLedgerCompany(SKS_LB_Setup_AutoCRParms.CustomerCompany ? SKS_LB_Setup_AutoCRParms.CustomerCompany : curext());
    element.setBRControlProperties();            
    element.enableDisableAutoPostInvNotFound();             //CR-9479
    element.EnableDisableReverseReconciliationJournal();    //CR-9689
    element.setLBControlProperties();                       //15966

    //CCA-Begin
    element.enableCCAllowDefaultCardToBeSet();
    element.enableCCProjSvcOrderFieldsToBeSet();
    element.enableCCModPaylinkFieldsToBeSet();
    element.setPositivePayTab();
    //CCA-End

    element.enablePCardFieldsToBeSet();

    element.enableDisableRegFields(); //10560

    element.enabledisablePayJournlfields();// FDD- 14323

    return ret;

}

public boolean validateWrite()
{
    boolean ret;
    SKS_LB_Import               cls_SKS_LB_Import  = new SKS_LB_Import();
    SKS_LB_Bank                 sks_lb_bank;
    SKS_LB_Bank                 sks_lb_bank2;
    System.Text.StringBuilder   sbDuplicateWarning = new System.Text.StringBuilder();
    System.Text.StringBuilder   sbFolderCreateWarning = new System.Text.StringBuilder();
    int                         unlockValue;
    ;

    if(SKS_PP_Setup.ModLockbox == NoYes::Yes)
    {
        if(SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue && !SKS_PP_Setup_LBBankFabricLocation.valuestr())
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS:SKGBankFabricFileLocationLabel", "@SKS352"));
            SKS_PP_Setup_LBBankFabricLocation.setFocus();
            return false;
        }

        if((SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue) && (!SKS_PP_Setup_LBPath.valueStr()))
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS343", "@SKS352"));
            SKS_PP_Setup_LBPath.setFocus();
            return false;
        }


        if(SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue && (!SKS_PP_Setup_LBBankFabricExtLocation.valueStr() && SKS_PP_Setup.LBSettlementMethod == SKS_LB_SettlementMethod::External))
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS:SKGBankFabricFileLocationExternal", "@SKS352"));
            SKS_PP_Setup_LBBankFabricExtLocation.setFocus();
            return false;
        }
        if((SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue) && (!SKS_PP_Setup_LBExternalPath.valueStr() && SKS_PP_Setup.LBSettlementMethod == SKS_LB_SettlementMethod::External))
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS881", "@SKS352"));
            SKS_PP_Setup_LBExternalPath.setFocus();
            return false;
        }

        if((SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue || SKS_EB_FileStorage.StorageOption_LB == SKS_EB_BankIntegOptWithNoLicense::NotLicensedEnumValue) && (SKS_PP_Setup_LBArchive.value() && !SKS_PP_Setup_LBPathArchive.valueStr()))
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS75", "@SKS352"));
            SKS_PP_Setup_LBPathArchive.setFocus();
            return false;
        }

        if(SKS_PP_Setup.MatchingRulesGroupId == '')
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS3225", "@SKS352"));
            SKS_PP_Setup_MatchingRulesGroupId.setFocus();
            return false;
        }

    }

    if(SKS_PP_Setup.ModPosPay == NoYes::Yes)
    {
        if( SKS_EB_FileStorage.StorageOption_PP == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            if(!SKS_PP_Setup_PPFileName.valueStr())
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS72", "@SKS39"));
                SKS_PP_Setup_PPFileName.setFocus();
                return false;
            }

            if(!SKS_PP_Setup_PPPath.valueStr())
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS74", "@SKS39"));
                SKS_PP_Setup_PPPath.setFocus();
                return false;
            }

            if(SKS_PP_Setup_PPArchive.value() && !SKS_PP_Setup_PPPathArchive.valueStr())
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SYS3335", "@SKS39"));
                SKS_PP_Setup_PPPathArchive.setFocus();
                return false;
            }
        }
        else if( SKS_EB_FileStorage.StorageOption_PP == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {

            if(!SKS_PP_Setup_PPBankFabricLocation.valueStr())
            {
                warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS:SKGBankFabricFileLocationLabel", "@SKS39"));
                SKS_PP_Setup_PPPath.setFocus();
                return false;
            }

        }
        if( SKS_PP_Setup.PPOutCommID == '')
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS2430", "@SKS39"));
            SKS_PP_Setup_CommID.setFocus();
            return false;
        }
    }

    if(SKS_PP_Setup.ModBR == NoYes::Yes)
    {
        if(!SKS_PP_Setup.BRPath && SKS_EB_FileStorage.StorageOption_BR == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS509", "@SKS313"));
            SKS_PP_Setup_BRPath.setFocus();
            return false;
        }
        else if(!SKS_PP_Setup.BRBankFabricLocation && SKS_EB_FileStorage.StorageOption_BR == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS:SKGBankFabricFileLocationLabel", "@SKS313"));
            SKS_PP_Setup_BRBankFabricLocation.setFocus();
            return false;
        }

        if( SKS_PP_Setup.BRInCommID == '')
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS2715", "@SKS313"));
            SKS_PP_Setup_BRInCommID.setFocus();
            return false;
        }
        if(!SKS_PP_Setup.BRJournalName)
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS:SKGBRImportedJournalName", "@SKS313"));
            SKS_PP_Setup_BRJournalName.setFocus();
            return false;
        }
        if(SKS_PP_Setup.BRAutomaticallyPostGL)
        {
            LedgerJournalName ledgerJournalName = LedgerJournalName::find(SKS_PP_Setup.BRJournalName);

            if((ledgerJournalName.ApproveActive == NoYes::Yes || ledgerJournalName.WorkflowApproval == NoYes::Yes) && SKS_PP_Setup.BRAutomaticallyPostGL)
            {
                SKS_PP_Setup.BRAutomaticallyPostGL = NoYes::No;
                error(strFmt("@SKS:SCFInvJrnlValidation", SKS_PP_Setup.BRJournalName));
                SKS_PP_Setup_BRJournalName.setFocus();
                return false;
            }
        }
        if(SKS_PP_Setup.BRAutoPostManualEntries)
        {
            LedgerJournalName ledgerJournalName = LedgerJournalName::find(SKS_PP_Setup.BRManualJournalName);

            if((ledgerJournalName.ApproveActive == NoYes::Yes || ledgerJournalName.WorkflowApproval == NoYes::Yes) && SKS_PP_Setup.BRAutoPostManualEntries)
            {
                SKS_PP_Setup.BRAutoPostManualEntries = NoYes::No;
                error(strFmt("@SKS:SCFInvJrnlValidation", SKS_PP_Setup.BRManualJournalName));
                SKS_PP_Setup_BRManualJournalName.setFocus();
                return false;
            }
        }
        if(SKS_PP_Setup.BRAutoPostCustSettlement)
        {
            LedgerJournalName ledgerJournalName = LedgerJournalName::find(SKS_PP_Setup.BRJournalName);

            if((ledgerJournalName.ApproveActive == NoYes::Yes || ledgerJournalName.WorkflowApproval == NoYes::Yes) && SKS_PP_Setup.BRAutoPostCustSettlement)
            {
                SKS_PP_Setup.BRAutomaticallyPostGL = NoYes::No;
                error(strFmt("@SKS:SCFInvJrnlValidation", SKS_PP_Setup.BRJournalName));
                SKS_PP_Setup_BRManualJournalName.setFocus();
                return false;
            }
        }
        if(SKS_PP_Setup.BRNotifyStatements == NoYes::Yes)
        {
            if(SKS_PP_Setup.BREmail == '' || SKS_PP_Setup.BRBatchJobId == 0)
            {

                if(SKS_PP_Setup.BREmail == '')
                {
                    warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1253", "@SKS313"));
                    SKS_PP_Setup_BREmail.setFocus();
                }
                else if(SKS_PP_Setup.BRBatchJobId == 0)
                {
                    warning(strFmt("@SKS:SKGTASParmsReqdField", "@SYS114515", "@SKS313"));
                    SKS_PP_Setup_BRBatchJobId.setFocus();
                }
                return false;
            }
        }

        //BRGLCreationCompany is hidden when "General ledger - advanced" is unchecked in License configuration
        // so it can't be set by the user.
        if(!isConfigurationkeyEnabled(configurationKeyNum(LedgerAdvIntercompanyAccounting)))
        {
            SKS_PP_Setup.BRGLCreationCompany = SKS_PP_Setup.company();
            SKS_PP_Setup.CCRGLCreationCompany = SKS_PP_Setup.company(); //SKS_VSTS_14296
        }
    }
    // SKS_FDD_13903 -->
    if(SKS_PP_Setup.ModCCReconciliationExtensions == NoYes::Yes)
    {
        if(!SKS_PP_Setup.CCRInCommID)
        {
            warning("@SKS253");
            SKS_PP_Setup_CCRInCommID.setFocus();
            return false;
        }
        if(!SKS_PP_Setup.CCRPath && SKS_EB_FileStorage.StorageOption_CCR == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            warning("@SKS253");
            SKS_PP_Setup_CCRPath.setFocus();
            return false;
        }
        else if(!SKS_PP_Setup.CCRBankFabricLocation && SKS_EB_FileStorage.StorageOption_CCR == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            warning("@SKS253"); //required field
            SKS_PP_Setup_CCRBankFabricLocation.setFocus();
            return false;
        }
        if(!SKS_PP_Setup.CCRCurrencyAdjustmentGainAccount)
        {
            warning("@SKS253");
            CCRCurrencyAdjustmentGainAccount.setFocus();
            return false;
        }
        if(!SKS_PP_Setup.CCRCurrencyAdjustmentLossAccount)
        {
            warning("@SKS253");
            CCRCurrencyAdjustmentLossAccount.setFocus();
            return false;
        }
        if(!SKS_PP_Setup.CCRCurrencyAdjustmentBankTranType)
        {
            warning("@SKS253");
            SKS_PP_Setup_CCRCurrencyAdjustmentBankTranType.setFocus();
            return false;
        }
        if(!SKS_PP_Setup.CCRGLCreationCompany)
        {
            warning("@SKS253");
            SKS_PP_Setup_CCRGLCreationCompany.setFocus();
            return false;
        }
    }
    // SKS_FDD_13903 <--
    if(SKS_PP_Setup.RegAPEFT == NoYes::Yes || SKS_PP_Setup.RegAREFT == Noyes::Yes)
    {
        if(SKS_PP_Setup.EFTLicensedFormats == 0)
        {
            warning(strFmt("@SKS:SKGTASParmsReqdField", "@SKS1420", "@SKS1028"));
            Registration.setFocus();
            SKS_PP_Setup_EFTLicensedFormats.setFocus();
            return false;
        }
    }

    if(SKS_PP_Setup.ModSCF == NoYes::Yes)
    {
        LedgerJournalName  invledgerJournalName, payledgerJournalName;

        if(SKS_PP_Setup.SCFOutCommID == '')  //FDD-14323- Supply chain finance
        {
            ret = false;
            SKS_PP_Setup_SCFOutCommID.setFocus();
            error("@SKS:SCFOutCommValidation");
        }

        if(SKS_PP_Setup.SCFAutoPmtInvPost == NoYes::Yes) //FDD-14323- Supply chain finance
        {
            select  ApproveActive, WorkflowApproval, JournalName from invledgerJournalName where invledgerJournalName.JournalName == SKS_PP_Setup.SCFInvoiceJournalName;
            if(invledgerJournalName.ApproveActive == NoYes::Yes || invledgerJournalName.WorkflowApproval == NoYes::Yes)
            {
                error(strFmt("@SKS:SCFInvJrnlValidation", SKS_PP_Setup.SCFInvoiceJournalName));
                SKS_PP_Setup_SCF_InvoiceJournalName.setFocus();
                return false;
            }
            select  ApproveActive, WorkflowApproval, JournalName from payledgerJournalName where payledgerJournalName.JournalName == SKS_PP_Setup.SCFPaymentJournalName;
            if(payledgerJournalName.ApproveActive == NoYes::Yes || payledgerJournalName.WorkflowApproval == NoYes::Yes)
            {
                error(strFmt("@SKS:SCFInvJrnlValidation", SKS_PP_Setup.SCFPaymentJournalName));
                SKS_PP_Setup_SCF_PaymJournalName.setFocus();
                return false;
            }
        }
    }

    unlockValue = element.validateUnlockKey();

    if(unlockValue == -1)
    {
        warning("@SKS246");
        SKS_PP_Setup_RegUnlockKey.setFocus();
        return false;
    }
    else if(unlockValue == -2)
    {
        box::info("@SKS1443");
        sks_pp_setup.EFTLicensedFormats = 0;
        SKS_PP_Setup_EFTLicensedFormats.setFocus();
        return false;
    }


    if ( gChangedLBPath == true)
    {
        // Create the In Process folder.
        if ( cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::Create, SKS_PP_Setup.LBPath, SKS_PP_Setup.LBAutoServerSide) != '')
        {
            sbFolderCreateWarning.Append("@SKS3172");
            sbFolderCreateWarning.Replace('%1', cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::InProcess_FolderName, SKS_PP_Setup.LBPath, SKS_PP_Setup.LBAutoServerSide));
            sbFolderCreateWarning.Replace('%2', cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::Complete_FolderName, SKS_PP_Setup.LBPath, SKS_PP_Setup.LBAutoServerSide));
            sbFolderCreateWarning.Replace('%2', cls_SKS_LB_Import.CheckLBFolders(SKS_LB_In_Process::Errors_FolderName, SKS_PP_Setup.LBPath, SKS_PP_Setup.LBAutoServerSide));
            sbFolderCreateWarning.Replace('%4', SKS_PP_Setup.LBPath);

            warning(sbFolderCreateWarning.ToString());

            SKS_PP_Setup_LBPath.setFocus();

            return false;
        }

        // Each BankAcct must be configured to use a unique LockBox Path
        // See if any bank acct is configured to use setups path
        select firstonly BankAccountId, LBFormatId from sks_lb_bank
            // get one with a lockbox format id and using the setup lb path
            // (technically there should only ever be one of these if any at all)
            where sks_lb_bank.LBFormatId != '' && sks_lb_bank.LBFromSetup == true
            // Now find one not using setups path
            join firstonly BankAccountId, LBFormatId  from sks_lb_bank2
                // get one with a lockbox format id and using their own lb path
                where sks_lb_bank2.LBFormatId != '' && sks_lb_bank2.LBFromSetup == false &&
                    // and it's own lb path matches the setup lb path
                    sks_lb_bank2.LBPath == SKS_PP_Setup_LBPath.text();

        if (sks_lb_bank && sks_lb_bank2)    // We found some accounts sharing the setup lockbox path
        {
            sbDuplicateWarning.Append("@SKS969");   // The Path to lockbox files for Bank Accounts must be unique.
            sbDuplicateWarning.Append(' '); // a space
            // The path for eBanking Suite Setup cannot match the path from Bank Account %1 because Bank Account %2 is set to use the path from eBanking Suite Setup.
            sbDuplicateWarning.Append("@SKS970");
            sbDuplicateWarning.Replace('%1', sks_lb_bank2.BankAccountId);
            sbDuplicateWarning.Replace('%2', sks_lb_bank.BankAccountId);
            sbDuplicateWarning.Replace('%3', sks_lb_bank.LBFormatId);
            sbDuplicateWarning.Replace('%4', sks_lb_bank2.LBFormatId);
            warning(sbDuplicateWarning.ToString());

            SKS_PP_Setup_LBPath.setFocus();

            return false;
        }
    }

    // CR-9689 -->
    if(SKS_PP_Setup.BRAllowReverseReconciliation && SKS_PP_Setup.BRReverseReconcilJournalName == '')
    {
        ret = checkFailed(strFmt("@SKS:SKGJournalNameMustVal"));
    }
    // CR-9689 <--
    // SKS_FDD_9602 - Start
    if(SKS_PP_Setup.IHB_Active && !SKS_PP_Setup.IHB_InterCoMOP)
    {
        warning("@SKS253");
        SKS_PP_Setup_IHB_InterCoMOP.setFocus();
        return false;
    }
    // SKS_FDD_9602 - End

    // BUG 10202 -->
    if (SKS_EFT_Setup.EFTAP_PaymRequest == NoYes::Yes && SKS_EFT_Setup.EFTAP_PaymRequestJournalName =='')
    {
        ret = checkFailed("@SKS:SKGReqJournalValidate");
        SKS_EFT_Setup_EFTAP_PaymRequestJournalName.setFocus();
        return false;
    }
    // BUG 10202 <--
    // Bug 10369 -->
    if(SKS_PP_Setup.IHB_InterestCalcFreq == SKS_IHB_InterestCalcFreq::Annual)
    {
        if(SKS_PP_Setup.IHB_CalculationFrequencyStartMonth == MonthsOfYear::None)
        {
            warning("@SKS253"); //required field
            IHB_InterestCalc_IHB_CalculationFrequencyStartMonth.setFocus();
            return false;
        }
        if(SKS_PP_Setup.IHB_CalculationFrequencyEndMonth == MonthsOfYear::None)
        {
            warning("@SKS253"); //required field
            IHB_InterestCalc_IHB_CalculationFrequencyEndMonth.setFocus();
            return false;
        }
        if (SKS_PP_Setup.IHB_CalculationFrequencyEndMonth - SKS_PP_Setup.IHB_CalculationFrequencyStartMonth != #ValidAnnualPeriod2
            && SKS_PP_Setup.IHB_CalculationFrequencyStartMonth - SKS_PP_Setup.IHB_CalculationFrequencyEndMonth != #ValidAnnualPeriod2
            && SKS_PP_Setup.IHB_CalculationFrequencyEndMonth - SKS_PP_Setup.IHB_CalculationFrequencyStartMonth != #ValidAnnualPeriod1)
        {
            warning("@SKS:SKGInterestCalculationInvalidAnnualPeriod"); //Starting and ending months should cover the period of an year.
            IHB_InterestCalc_IHB_CalculationFrequencyEndMonth.setFocus();
            return false;
        }
    }
    // Bug 10369 <--

    if(SKS_PP_Setup.IHB_Active)
    {
        LedgerJournalName payledgerJournalName;

        select  ApproveActive, WorkflowApproval, JournalName from payledgerJournalName where payledgerJournalName.JournalName == SKS_PP_Setup.IHBVendorNettingJournalName;
        if(payledgerJournalName.ApproveActive == NoYes::Yes || payledgerJournalName.WorkflowApproval == NoYes::Yes)
        {
            error(strFmt("@SKS:SCFInvJrnlValidation", SKS_PP_Setup.IHBVendorNettingJournalName));
            SKS_PP_Setup_IHBJournalName.setFocus();
            return false;
        }
    }


    ret = super();

    return ret;
}

/// <summary>
/// Initalize the SKS_PP_Setup datasource.
/// </summary>
public void init()
{
    super();
    //Always Advanced BR
    SKS_PP_Setup.BREdition = SKS_BR_Edition::Advanced;
    SKS_PP_Setup_ds.cacheAddMethod('referencelabel');
    SKS_PP_Setup_ds.cacheAddMethod('referenceSameAsLabel');
    SKS_PP_Setup_DS.cacheAddMethod('referencehelp');

    //SKS_PP_Setup.BREdition is always ADVANCED and it is never to change.
    SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup,BREdition)).allowEdit(false);
}

/// <summary>
///  Method returns the expiration date of the currently entered registration key.
/// </summary>
[SysClientCacheDataMethodAttribute(true)]
display date getExpirationDate()
{
    date retval = dateNull();
    if(SKS_PP_Setup.RegUnlockKey)
    {
        SKS_EB_SysInfo locSKS_EB_SysInfo = new SKS_EB_SysInfo();
        retval = locSKS_EB_SysInfo.getExpirationDate(conPeek(str2con(SKS_PP_Setup.RegUnlockKey,':'),1));
    }
    return retval;
}

/// <summary>
///
/// </summary>
[SysClientCacheDataMethodAttribute(true)]
display str getCurrentActiveFormats()
{
    return strFmt("@SKS:SKGCurrentOutboundInbound",
        SKS_EFT_FileFormat::getActiveOutboundFormatCount(),
        SKS_EFT_InboundFileFormat::getActiveInboundFormatCount());
}

/// <summary>
/// When the user count exceeds the licensed users, you have 30 grace days. This method returns the cutoff for those grace days
/// </summary>
/// <returns>Date TAS will cease functioning due to user count license violation</returns>
[SysClientCacheDataMethodAttribute(true)]
display date getUserGraceEnd()
{
    return SKS_PP_Setup.RegDateUserCountWasExceeded == dateNull() ? dateNull() : SKS_PP_Setup.RegDateUserCountWasExceeded + 30;
}

/// <summary>
/// Write the SKS_PP_Setup buffer's current values to the database.
/// </summary>
public void write()
{
    SKS_PP_Setup setup = SKS_PP_Setup;
    SKS_PP_Setup setup_orig = setup.orig();

    super();

    if ((!setup_orig.ModBR && setup.ModBR)
        || (!setup_orig.ModLockbox && setup.ModLockbox))
    {
        SKS_LB_SecondaryMatch::initializeInvoiceMatchTable();
    }
}

/// <summary>
/// Method to validate the workflow journal
/// </summary>
public boolean validateWorkFlowJournal()
{
    boolean ret = true;
    LedgerJournalName  lLedgerJournalName;

    if(SKS_PP_Setup.AutoSubmitWF == NoYes::Yes && ret)
    {
        select firstonly lLedgerJournalName where lLedgerJournalName.journalname == SKS_PP_Setup.JournalName;
        if(lLedgerJournalName.WorkFlowApproval == NoYes::No)
        {
            Error(strFmt("@SKS:SKGWFNotEnabled", SKS_PP_Setup.JournalName));
            ret = false;
        }
    }
    return ret;
}

/// <summary>
/// Bank Rec Method to update the Journal name utilized in Autoposting
/// </summary>
public void UpdateJournalNameAutoPost()
{
    LedgerJournalName   locLedgerJournalName;
    changecompany(SKS_PP_Setup.BRGLCreationCompany)
    {
        ttsbegin;
        locLedgerJournalName = LedgerJournalName::find(SKS_PP_Setup.BRJournalName, true);
        locLedgerJournalName.SKS_BR_AutoPost = SKS_PP_Setup.BRAutomaticallyPostGL;
        locLedgerJournalName.update();
        ttscommit;
    }
}

