}

public boolean validate()
{
    boolean ret;
    int maxVal = 365;
    int minVal = 0;
    ;

    if(SKS_PP_Setup.BRDateTolerance < minVal)
    {
        Box::stop(strFmt("@SKS239",minVal));
        return false;
    }
    if(SKS_PP_Setup.BRDateTolerance > maxVal)
    {
        Box::stop(strFmt("@SKS240",maxVal));
        return false;
    }
    ret = super();

    return ret;
}

