public boolean validate()
{
    boolean ret = true;
    LedgerJournalName ledgerJournalName;
    ;

    changeCompany(SKS_PP_Setup.BRGLCreationCompany)
    {
        if(SKS_PP_Setup.BRJournalName)
        {
            ledgerJournalName = LedgerJournalName::find(SKS_PP_Setup.BRJournalName);

            if(ledgerJournalName.RecId == 0)
            {
                box::info(strFmt("@SYS22462",SKS_PP_Setup.BRJournalName) ,"@SKS225");
                return false;
            }
            else
            {
                if(ledgerJournalName.JournalType != LedgerJournalType::Daily)
                {
                    box::info(strFmt("@SYS114718",SKS_PP_Setup.BRJournalName, enum2str(LedgerJournalType::Daily)),"@SKS225");
                    return false;
                }
            }
        }
        else
        {
            warning(strFmt("@SKS:SKGBRImportJournNameValidation"));
            SKS_PP_Setup_BRJournalName.setFocus();
            return false;
        }
    }

    return ret;
}

