public boolean validate()
{
    boolean ret;
    SKS_PP_Bank locSKS_PP_Bank;
    SKS_PP_Setup locSKS_PP_Setup;
    SKS_BR_File locSKS_BR_File;
    str locErrorMessage;



    SKS_PP_Setup.BRPath = SKS_BR_File::removeDoubleBackslash(SKS_PP_Setup.BRPath);
    SKS_PP_Setup.BRPath = strLTrim(SKS_PP_Setup.BRPath);
    if(strLen(SKS_PP_Setup.BRPath) == 0)//17701 if no path supplied, don't try to validate it.
    {
        return true;
    }
    SKS_PP_Setup.BRPath = SKS_BR_File::removeLastPathSlash(SKS_PP_Setup.BRPath);
    SKS_PP_Setup.BRPath = strLTrim(SKS_PP_Setup.BRPath);
    ret = super();

    if(ret)//Check directory existance
    {
        ret = SKS_EB_FileHub::directoryExistsForm(SKS_PP_Setup.BRPath);
    }
    if(ret)//Check that the directory is not used in another org hierarchy
    {
        locSKS_PP_Setup.BRPath = SKS_PP_Setup.BRPath;
        locSKS_PP_Setup.BRBankFabricLocation = SKS_PP_Setup.BRBankFabricLocation;
        locSKS_BR_File = SKS_BR_File::construct(SKS_EB_FileStorage.StorageOption_BR ==  SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue ? SKS_BF_BankIntType::BankFabric : SKS_BF_BankIntType::FileHubLocal);
        locErrorMessage = locSKS_BR_File.validateBRPath(locSKS_PP_Bank,locSKS_PP_Setup,true,SKS_EB_FileStorage);
        if(strLen(locErrorMessage) > 0)
        {
            ret = false;
            Error(locErrorMessage);
        }
    }
    return ret;
}

