    super();

    if(SKS_PP_Setup.FFTPM == NoYes::Yes)
    {
        TPMDedCatSetup.enabled(true);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, TPMRemainShortpaid)).allowedit(true);
    }
    else
    {
        TPMDedCatSetup.enabled(false);
        SKS_PP_Setup_ds.object(fieldNum(SKS_PP_Setup, TPMRemainShortpaid)).allowedit(false);
        SKS_PP_Setup.TPMRemainShortpaid = false;
    }
}

public boolean validate()
{
    SKS_LB_Bank sks_lb_bank;
    CustPaymModeTable   lCustPaymModeTable;
    SKS_LB_FileFormat   lFileFormat;
    boolean ret;

    ret = super();

    if(!tableName2Id('FoxMpyDeductionCategoryWriteoff'))
    {
        ret = false;
        error("@SKS3913");
    }

    if(SKS_PP_Setup.FFTPM == NoYes::Yes && SKS_PP_Setup.LBStandardDed == NoYes::Yes)
    {
        ret = false;
        error("@SKS:SKGFFTPMNotValid");
    }

    if(ret)
    {
        if(SKS_PP_Setup.SepPaymByInv)
        {
            error("@SKS3901");
            ret = false;
        }
        else
        {
            select firstonly sks_lb_bank where sks_lb_bank.SepPaymByInv == NoYes::Yes;
            if(SKS_LB_bank)
            {
                error(strFmt("@SKS3901", sks_lb_bank.BankAccountId, sks_lb_bank.LBFormatId));
                ret = false;
            }

            if(ret)
            {
                select firstonly lCustPaymModeTable where lCustPaymModeTable.PaymMode == 'DED';
                if(!lCustPaymModeTable)
                {
                    error("@SKS3903");
                    ret = false;
                }
                else
                {
                    if(lCustPaymModeTable.PaymentLedgerDimension == 0)
                    {
                        error("@SKS3906");
                        ret = false;
                    }
                }
            }

            if(ret)
            {
                if(!SKS_PP_Setup.FFTPM)
                {
                    select firstonly lFileFormat where lFileformat.CombineADXRefNbr == NoYes::Yes;
                    if(lFileFormat)
                    {
                        error(strFmt("@SKS3921", lFileFormat.FormatID));
                        ret = false;
                    }
                }
            }
        }
    }
    return ret;
}

/// <summary>
/// Daily journals
/// </summary>
/// <param name = "_formControl"></param>
/// <param name = "_filterStr"></param>
public void lookup(FormControl _formControl, str _filterStr)
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRangeJournalType;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup =
        SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, JournalName));
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, Name));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));
    queryBuildRangeJournalType = queryBuildDataSource.addRange(
        fieldNum(LedgerJournalName, JournalType));
    queryBuildRangeJournalType.value('0');

    queryBuildDataSource.addRange(fieldNum(LedgerJournalName, WorkflowApproval)).value('0');
    queryBuildDataSource.addRange(fieldNum(LedgerJournalName, ApproveActive)).value('0');

    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
}

