/// <summary>
/// Override lookup method to show only "Inbound" direction from SKS_EB_CommProcess table
/// </summary>
/// <param name = "_formControl">_formControl</param>
/// <param name = "_filterStr">_filterStr</param>
public void lookup(FormControl _formControl, str _filterStr)
{
    //<ISSUE-19724: the LBInboundCommID field lookup needs to show only communication ids with Module = Ares and Direction = Inbound
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(SKS_EB_CommProcess), _formControl);

    //super(_formControl, _filterStr);

    sysTableLookup.addLookupField(fieldNum(SKS_EB_CommProcess, CommID));
    sysTableLookup.addLookupField(fieldNum(SKS_EB_CommProcess, Description));

    queryBuildDataSource = query.addDataSource(tableNum(SKS_EB_CommProcess));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EB_CommProcess, Direction));
    queryBuildRange.value(enum2Str(SKS_EB_Direction::Inbound));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EB_CommProcess, Module));
    queryBuildRange.value(enum2Str(SKS_EB_Module::Lockbox));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
    //</ISSUE-19724>
}

