        catch
        {
            error(strfmt("@Dimension:DimensionEntitySyncMissingDimensionAttribute", strFmt("@ElectronicReportingMapping:NumberSequenceWithName",extendedTypeId2name(extendedTypeNum(TAMDeductionID)))));
            ret = false;
        }
    }

    return ret;
}

