public boolean validate()
{
    boolean ret;
    int thisval;
    System.Text.StringBuilder sb = new System.Text.StringBuilder();

    ;

    thisval = str2int(SKS_PP_Setup_PPFilePeriodsRet.valueStr());

    if(thisval < 2 )
    {
        sb.Append("@SKS239");
        sb.Replace('%1','2');
        Box::stop(sb.ToString(),"@SKS225"); //must be greater than 2

        SKS_PP_Setup_PPFilePeriodsRet.setFocus();

        return false;
    }
    else if(thisval > 120 )
    {
        sb.Append("@SKS240");
        sb.Replace('%1','120');
        Box::stop(sb.ToString(),"@SKS225"); //musst be less than 120

        SKS_PP_Setup_PPFilePeriodsRet.setFocus();

        return false;
    }

    ret = super();


    return ret;
}

